// src/contexts/AuthContext.jsx
import React, { createContext, useState, useEffect, useCallback } from 'react';
import { checkAuth, login as apiLogin, logout as apiLogout, register as apiRegister } from '../services/auth';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  const verifyAuth = useCallback(async () => {
    setLoading(true);
    try {
      console.log('[DEBUG] Chamando checkAuth()...');
      const response = await checkAuth();
      console.log('[DEBUG] Resposta de checkAuth:', response);

      if (response && response.data && response.data.user) {
        console.log('[DEBUG] Usuário autenticado:', response.data.user);
        setUser(response.data.user);
        return true;
      } else {
        console.warn('[DEBUG] Nenhum usuário retornado em checkAuth.');
        setUser(null);
        return false;
      }
    } catch (error) {
      console.error('[DEBUG] Erro durante verifyAuth:', error);
      setUser(null);
      return false;
    } finally {
      setLoading(false);
      console.log('[DEBUG] Finalizando verifyAuth - loading:', false);
    }
  }, []);

  const login = useCallback(async (credentials) => {
    try {
      const response = await apiLogin(credentials);
      console.log('[DEBUG] Resposta de login:', response);

      setUser(response.user);
      localStorage.setItem('token', response.token);
      return { success: true };
    } catch (error) {
      console.error('[DEBUG] Erro durante login:', error);
      return { success: false, error: error.message };
    }
  }, []);

  const register = useCallback(async (userData) => {
    try {
      const response = await apiRegister(userData);
      console.log('[DEBUG] Resposta de register:', response);

      setUser(response.user);
      localStorage.setItem('token', response.token);
      return { success: true };
    } catch (error) {
      console.error('[DEBUG] Erro durante register:', error);
      return { success: false, error: error.message };
    }
  }, []);

  const logout = useCallback(async () => {
    try {
      await apiLogout();
      console.log('[DEBUG] Logout realizado com sucesso.');
      setUser(null);
      localStorage.removeItem('token');
      return { success: true };
    } catch (error) {
      console.error('[DEBUG] Erro durante logout:', error);
      return { success: false, error: error.message };
    }
  }, []);

  useEffect(() => {
    console.log('[DEBUG] Iniciando verificação de autenticação...');
    verifyAuth();
  }, [verifyAuth]);

  useEffect(() => {
    console.log('[DEBUG] Estado atualizado - user:', user);
    console.log('[DEBUG] Estado atualizado - loading:', loading);
  }, [user, loading]);

  return (
    <AuthContext.Provider value={{ 
      user, 
      loading, 
      login, 
      logout, 
      register, 
      verifyAuth 
    }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = React.useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth deve ser usado dentro de um AuthProvider');
  }
  return context;
};

export default AuthContext;
