// src/services/auth.js
import api from './api';

export const register = async (userData) => {
  try {
    const { data } = await api.post('/auth/register', userData, {
      _noAuth: true // Flag para evitar interceptor
    });
    return data;
  } catch (error) {
    console.error('[register] Erro:', error);
    if (error.response?.status === 409) {
      throw { 
        type: 'CONFLICT',
        message: error.response.data.message,
        conflicts: error.response.data.conflicts
      };
    }
    throw {
      type: 'UNKNOWN_ERROR',
      message: error.response?.data?.message || 'Erro ao registrar usuário'
    };
  }
};

// ... (outras funções mantêm a mesma implementação)

// Função para login de usuário
export const login = async (credentials) => {
  try {
    const { data } = await api.post(`${API_URL}/login`, credentials);
    return data;
  } catch (error) {
    console.error('[login] Erro:', error);
    if (error.response && error.response.status === 401) {
      throw { type: 'AUTH_ERROR', message: 'Credenciais inválidas' };
    }
    throw { type: 'UNKNOWN_ERROR', message: 'Erro ao fazer login' };
  }
};

// Função para verificar autenticação
export const checkAuth = async () => {
  try {
    const { data } = await api.get(`${API_URL}/check`);
    return data;
  } catch (error) {
    console.error('[checkAuth] Erro:', error);
    if (error.response && error.response.status === 401) {
      throw { type: 'AUTH_ERROR', message: 'Sessão expirada' };
    }
    throw { type: 'UNKNOWN_ERROR', message: 'Erro ao verificar autenticação' };
  }
};

// Função para logout de usuário
export const logout = async () => {
  try {
    localStorage.removeItem('token');
    // Se no futuro quiser enviar algo para /auth/logout no backend, é aqui
    return { success: true };
  } catch (error) {
    console.error('[logout] Erro:', error);
    throw { type: 'UNKNOWN_ERROR', message: 'Erro ao fazer logout' };
  }
};
