// src/components/profile/UserMemes.jsx
import { useState, useEffect } from 'react';
import styled from 'styled-components';
import api from '../../services/api';
import MemeThumbnail from '../MemeThumbnail';

const MemeGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
  gap: 20px;
  margin-top: 20px;
`;

const RepostBadge = styled.div`
  position: absolute;
  top: 5px;
  left: 5px;
  background: rgba(0,0,0,0.7);
  color: white;
  padding: 3px 6px;
  border-radius: 4px;
  font-size: 0.8rem;
`;

const UserMemes = ({ userId }) => {
  const [memes, setMemes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMemes = async () => {
      try {
        const { data } = await api.get(`/users/${userId}/memes?includeReposts=true`);
        setMemes(data);
      } catch (error) {
        console.error('Error fetching user memes:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchMemes();
  }, [userId]);

  if (loading) return <div>Loading memes...</div>;

  return (
    <MemeGrid>
      {memes.map(meme => (
        <div key={meme._id} style={{ position: 'relative' }}>
          {meme.isRepost && <RepostBadge>Repostado</RepostBadge>}
          <MemeThumbnail meme={meme} />
        </div>
      ))}
    </MemeGrid>
  );
};

export default UserMemes
