// src/components/profile/ProfileTabs.jsx
import styled from 'styled-components';

const TabsContainer = styled.div`
  display: flex;
  border-bottom: 1px solid #eee;
  margin-bottom: 20px;
`;

const TabButton = styled.button`
  padding: 10px 20px;
  background: none;
  border: none;
  border-bottom: 2px solid ${props => props.$active ? '#ff4500' : 'transparent'};
  color: ${props => props.$active ? '#ff4500' : '#666'};
  font-weight: ${props => props.$active ? 'bold' : 'normal'};
  cursor: pointer;
  transition: all 0.2s;

  &:hover {
    color: #ff4500;
  }
`;

const ProfileTabs = ({ activeTab, setActiveTab }) => {
  const tabs = [
    { id: 'memes', label: 'Posts' },
    { id: 'likes', label: 'Likes' },
    { id: 'activity', label: 'Activity' },
    { id: 'guests', label: 'Guests' },
    { id: 'settings', label: 'Settings' }
  ];
  {activeTab === 'settings' && currentUser?._id === userId && (
    <ProfileSettings user={profileData.user} />
  )}

  return (
    <TabsContainer>
      {tabs.map(tab => (
        <TabButton
          key={tab.id}
          $active={activeTab === tab.id}
          onClick={() => setActiveTab(tab.id)}
        >
          {tab.label}
        </TabButton>
      ))}
    </TabsContainer>
  );
};

export default ProfileTabs;
