import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../../contexts/AuthContext';
import styled from 'styled-components';

const Nav = styled.nav`
  background: #ff4500;
  padding: 1rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
  position: sticky;
  top: 0;
  z-index: 1000;
`;

const NavLinks = styled.div`
  display: flex;
  gap: 1rem;
`;

const NavButton = styled(Link)`
  color: white;
  text-decoration: none;
  padding: 0.5rem 1rem;
  border-radius: 4px;
  transition: background-color 0.2s;
  &:hover {
    background: rgba(255, 255, 255, 0.2);
  }
`;

const LogoutButton = styled.button`
  color: white;
  background: none;
  border: none;
  padding: 0.5rem 1rem;
  border-radius: 4px;
  cursor: pointer;
  font-family: inherit;
  font-size: inherit;
  transition: background-color 0.2s;
  &:hover {
    background: rgba(255, 255, 255, 0.2);
  }
`;

const Navbar = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = async () => {
    const { success } = await logout();
    if (success) {
      navigate('/');
    }
  };

  return (
    <Nav>
      <Link to="/" style={{ color: 'white', textDecoration: 'none', fontWeight: 'bold', fontSize: '1.2rem' }}>
        iFunny Clone
      </Link>
      <NavLinks>
        {user ? (
          <>
            <NavButton to="/upload">Criar Post</NavButton>
            <NavButton to={`/users/${user._id}`}>Meu Perfil</NavButton>
            <LogoutButton onClick={handleLogout}>Sair</LogoutButton>
          </>
        ) : (
          <>
            <NavButton to="/login">Login</NavButton>
            <NavButton to="/register">Registrar</NavButton>
          </>
        )}
      </NavLinks>
    </Nav>
  );
};

export default Navbar;
