// src/components/profile/UserGuests.jsx
import { useState, useEffect } from 'react';
import styled from 'styled-components';
import api from '../../services/api';

const GuestList = styled.div`
  margin-top: 20px;
`;

const GuestItem = styled.div`
  display: flex;
  align-items: center;
  padding: 10px;
  border-bottom: 1px solid #eee;
`;

const GuestAvatar = styled.img`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  margin-right: 15px;
`;

const UserGuests = ({ userId }) => {
  const [guests, setGuests] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchGuests = async () => {
      try {
        const { data } = await api.get(`/users/${userId}/guest-history`);
        setGuests(data);
      } catch (error) {
        console.error('Error fetching guests:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchGuests();
  }, [userId]);

  if (loading) return <div>Loading guests...</div>;

  return (
    <GuestList>
      {guests.map(guest => (
        <GuestItem key={guest._id}>
          <GuestAvatar src={guest.visitor.profile.avatar || '/default-avatar.jpg'} />
          <span>@{guest.visitor.username}</span>
        </GuestItem>
      ))}
    </GuestList>
  );
};

export default UserGuests;
