import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import api from '../../services/api';
import UserCard from './UserCard';

const FollowersContainer = styled.div`
  margin-top: 20px;
`;

const LoadingMessage = styled.div`
  padding: 20px;
  text-align: center;
  color: var(--text-light);
`;

const ErrorMessage = styled.div`
  padding: 20px;
  text-align: center;
  color: var(--danger);
`;

const UserFollowers = ({ userId, followersCount }) => {
  const [followers, setFollowers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [actualCount, setActualCount] = useState(followersCount);

  useEffect(() => {
    const fetchFollowers = async () => {
      try {
        setLoading(true);
        const { data } = await api.get(`/users/${userId}/followers`);
        setFollowers(data.data);
        // Atualiza a contagem real com base nos dados recebidos
        setActualCount(data.data.length);
      } catch (error) {
        setError(error.response?.data?.message || 'Erro ao carregar seguidores');
      } finally {
        setLoading(false);
      }
    };

    fetchFollowers();
  }, [userId]);

  if (loading) return <LoadingMessage>Carregando seguidores...</LoadingMessage>;
  if (error) return <ErrorMessage>{error}</ErrorMessage>;

  return (
    <FollowersContainer>
      <h3>{actualCount} Seguidor{actualCount !== 1 ? 'es' : ''}</h3>
      {followers.length > 0 ? (
        followers.map(follower => (
          <UserCard 
            key={follower.follower._id} 
            user={follower.follower}  // Ajuste para acessar o objeto follower corretamente
            showFollowButton={true}
          />
        ))
      ) : (
        <p>Nenhum seguidor encontrado</p>
      )}
    </FollowersContainer>
  );
};

export default UserFollowers;
