import React, { useState, useEffect, useCallback, memo, useRef, useMemo } from 'react';
import styled, { keyframes, css } from 'styled-components';
import api from '../services/api';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import { 
  FiSend, 
  FiCornerDownLeft, 
  FiMoreHorizontal,
  FiEdit2,
  FiTrash2,
  FiX,
  FiPaperclip,
  FiImage
} from 'react-icons/fi';
import PropTypes from 'prop-types';
const buildUrl = (url) => {
  if (!url) {
    console.warn('URL is empty or undefined');
    return '';
  }

  // Se j치 for uma URL completa, retorna diretamente
  if (url.startsWith('http')) {
    return url;
  }

  // Adiciona o prefixo da API apenas se necess치rio
  const apiUrl = process.env.REACT_APP_API_URL || 'https://ifunnytest.loca.lt';
  const fullUrl = `${apiUrl}${url}`;
  
  return fullUrl;
};

// ============ Animations ============
const bounceHappy = keyframes`
  0%, 100% { transform: scale(1) rotate(0deg); }
  25% { transform: scale(1.2) rotate(5deg); }
  50% { transform: scale(1.3) rotate(0deg); }
  75% { transform: scale(1.2) rotate(-5deg); }
`;

const shakeAngry = keyframes`
  0%, 100% { transform: scale(1) rotate(0deg); }
  20% { transform: scale(1.2) rotate(-10deg); }
  40% { transform: scale(1.3) rotate(10deg); }
  60% { transform: scale(1.2) rotate(-10deg); }
  80% { transform: scale(1.1) rotate(5deg); }
`;

const pulse = keyframes`
  0% { transform: scale(1); }
  50% { transform: scale(1.1); }
  100% { transform: scale(1); }
`;

