import React from 'react';
import styled from 'styled-components';
import MemeUpload from '../components/MemeUpload';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useTheme } from '../styles/ThemeContext';

const UploadPageContainer = styled.div`
  max-width: 800px;
  margin: 2rem auto;
  padding: 2rem;
  background: var(--card-bg);
  border-radius: var(--radius-lg);
  box-shadow: var(--shadow);
`;

const Title = styled.h2`
  color: var(--text);
  margin-bottom: 1.5rem;
`;

const LoadingMessage = styled.div`
  color: var(--text-light);
  text-align: center;
  padding: 1rem;
`;

const SuccessMessage = styled.div`
  color: var(--success);
  text-align: center;
  padding: 1rem;
  background: rgba(16, 185, 129, 0.1);
  border-radius: var(--radius-sm);
  margin: 1rem 0;
`;

const UploadPage = () => {
  const { user, loading, initialCheckDone } = useAuth();
  const { theme } = useTheme();
  const navigate = useNavigate();
  const [uploadComplete, setUploadComplete] = React.useState(false);

  React.useEffect(() => {
    if (initialCheckDone && !user && !loading) {
      navigate('/login', { state: { from: '/upload' } });
    }
  }, [user, loading, initialCheckDone, navigate]);

  const handleUploadSuccess = () => {
    setUploadComplete(true);
    setTimeout(() => navigate('/feed'), 2000);
  };

  if (loading || !initialCheckDone) {
    return (
      <UploadPageContainer>
        <LoadingMessage>Carregando...</LoadingMessage>
      </UploadPageContainer>
    );
  }

  return (
    <UploadPageContainer>
      <Title>Criar Nova Postagem</Title>
      {uploadComplete ? (
        <SuccessMessage>
          Postagem criada com sucesso! Redirecionando...
        </SuccessMessage>
      ) : (
        <MemeUpload onSuccess={handleUploadSuccess} />
      )}
    </UploadPageContainer>
  );
};

export default UploadPage;
