import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { FiUser } from 'react-icons/fi';

const CardContainer = styled.div`
  display: flex;
  align-items: center;
  padding: 15px;
  border-bottom: 1px solid #eee;
`;

const Avatar = styled.img`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  object-fit: cover;
  margin-right: 15px;
`;

const UserInfo = styled.div`
  flex: 1;
`;

const Username = styled(Link)`
  font-weight: bold;
  text-decoration: none;
  color: inherit;
  display: block;
  margin-bottom: 5px;

  &:hover {
    text-decoration: underline;
  }
`;

const Bio = styled.p`
  font-size: 14px;
  color: #666;
  margin: 0;
`;

const FollowButton = styled.button`
  padding: 5px 15px;
  border-radius: 20px;
  border: 1px solid #ddd;
  background: ${props => props.isFollowing ? '#fff' : '#000'};
  color: ${props => props.isFollowing ? '#000' : '#fff'};
  cursor: pointer;
  font-weight: bold;

  &:hover {
    opacity: 0.9;
  }
`;

const UserCard = ({ user, showFollowButton = false }) => {
  return (
    <CardContainer>
      <Avatar 
        src={user.profile?.avatar || '/default-avatar.jpg'} 
        alt={user.username}
        onError={(e) => {
          e.target.src = '/default-avatar.jpg';
        }}
      />
      <UserInfo>
        <Username to={`/profile/${user._id}`}>@{user.username}</Username>
        <Bio>{user.profile?.bio || 'No bio yet'}</Bio>
      </UserInfo>
      {showFollowButton && (
        <FollowButton>
          {false ? 'Following' : 'Follow'}
        </FollowButton>
      )}
    </CardContainer>
  );
};

export default UserCard;
