// contexts/NotificationContext.jsx
import React, { createContext, useContext, useState, useEffect } from 'react';
import api from '../api';
import { useAuth } from './AuthContext';
import { listenForNotifications, initSocket } from '../services/socket';

const NotificationContext = createContext();

export const NotificationProvider = ({ children }) => {
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [loading, setLoading] = useState(true);
  const { user } = useAuth();

  // Carrega notificações iniciais
  const loadNotifications = async () => {
    try {
      setLoading(true);
      const response = await api.get('/notifications', {
        params: { limit: 20, skip: 0 }
      });
      setNotifications(response.data.data);
    } catch (error) {
      console.error('Failed to load notifications:', error);
    } finally {
      setLoading(false);
    }
  };

  // Carrega contagem de não lidas
  const loadUnreadCount = async () => {
    if (!user) return;
    try {
      const response = await api.get('/notifications/unread-count');
      setUnreadCount(response.data.count);
    } catch (error) {
      console.error('Failed to load unread count:', error);
    }
  };

  // Marca notificações como lidas
  const markAsRead = async (notificationIds) => {
    if (!notificationIds || notificationIds.length === 0) return;
    try {
      await api.post('/notifications/mark-read', { notificationIds });
      setUnreadCount(prev => Math.max(0, prev - notificationIds.length));
      setNotifications(prev => 
        prev.map(n => 
          notificationIds.includes(n._id) ? { ...n, read: true } : n
        )
      );
    } catch (error) {
      console.error('Failed to mark as read:', error);
    }
  };

  // Configura WebSocket quando o usuário loga
  useEffect(() => {
    if (!user) return;

    // Inicializa socket
    initSocket();

    // Configura listener para novas notificações
    const handleNewNotification = (notification) => {
      setNotifications(prev => [notification, ...prev]);
      setUnreadCount(prev => prev + 1);
      
      // Mostra toast/alert se a página não está em foco
      if (document.visibilityState !== 'visible') {
        showNotificationAlert(notification);
      }
    };

    listenForNotifications(handleNewNotification);

    // Carrega dados iniciais
    loadNotifications();
    loadUnreadCount();

    return () => {
      const socket = getSocket();
      if (socket) {
        socket.off('new-notification', handleNewNotification);
      }
    };
  }, [user]);

  // Função para mostrar alerta de notificação
  const showNotificationAlert = (notification) => {
    if ('Notification' in window && Notification.permission === 'granted') {
      const title = getNotificationTitle(notification);
      const options = {
        body: getNotificationBody(notification),
        icon: notification.sender?.profile?.avatar || '/logo192.png'
      };
      new Notification(title, options);
    }
  };

  // Funções auxiliares para texto de notificação
  const getNotificationTitle = (notification) => {
    switch(notification.type) {
      case 'like':
        return `${notification.sender?.username || 'Alguém'} curtiu seu meme`;
      case 'like_grouped':
        return `Seu meme tem ${notification.metadata?.count || 'muitas'} curtidas`;
      case 'comment':
        return `${notification.sender?.username || 'Alguém'} comentou no seu meme`;
      case 'comment_grouped':
        return `Seu meme tem ${notification.metadata?.count || 'muitos'} comentários`;
      // outros tipos...
      default:
        return 'Nova notificação';
    }
  };

  const getNotificationBody = (notification) => {
    switch(notification.type) {
      case 'like':
      case 'comment':
        return notification.meme?.caption 
          ? truncate(notification.meme.caption, 50) 
          : 'Veja agora';
      case 'like_grouped':
      case 'comment_grouped':
        return notification.meme?.caption 
          ? truncate(notification.meme.caption, 50) 
          : 'Veja todas as interações';
      default:
        return '';
    }
  };

  const truncate = (str, n) => {
    return str?.length > n ? str.substr(0, n-1) + '...' : str;
  };

  return (
    <NotificationContext.Provider
      value={{
        notifications,
        unreadCount,
        loading,
        markAsRead,
        loadNotifications,
        loadUnreadCount
      }}
    >
      {children}
    </NotificationContext.Provider>
  );
};

export const useNotifications = () => {
  const context = useContext(NotificationContext);
  if (!context) {
    throw new Error('useNotifications must be used within a NotificationProvider');
  }
  return context;
};
