// src/components/profile/ProfileHeader.jsx
import styled from 'styled-components';
import { FiEdit, FiUsers, FiUserPlus, FiUserCheck } from 'react-icons/fi';
import { useState, useEffect } from 'react';
import api from '../../services/api';
import UserFollowers from './UserFollowers';
import UserFollowing from './UserFollowing';

const HeaderContainer = styled.div`
  position: relative;
  margin-bottom: 30px;
`;

const CoverPhoto = styled.div`
  height: 300px;
  background-image: url(${props => props.src});
  background-size: cover;
  background-position: center;
  border-radius: 8px;
  position: relative;
`;

const ProfileInfo = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-top: -75px;
  position: relative;
  z-index: 1;
`;

const Avatar = styled.img`
  width: 150px;
  height: 150px;
  border-radius: 50%;
  border: 4px solid white;
  object-fit: cover;
`;

const Username = styled.h1`
  margin-top: 15px;
  font-size: 24px;
`;

const Bio = styled.p`
  margin-top: 10px;
  color: #666;
  max-width: 500px;
  text-align: center;
`;

const StatsContainer = styled.div`
  display: flex;
  gap: 30px;
  margin-top: 20px;
`;

const StatItem = styled.div`
  text-align: center;
  cursor: pointer;
  transition: all 0.2s;

  &:hover {
    opacity: 0.8;
  }
`;

const StatNumber = styled.div`
  font-weight: bold;
  font-size: 18px;
`;

const StatLabel = styled.div`
  font-size: 14px;
  color: #666;
`;

const EditButton = styled.button`
  position: absolute;
  top: 20px;
  right: 20px;
  background: rgba(0,0,0,0.5);
  color: white;
  border: none;
  padding: 8px 15px;
  border-radius: 20px;
  display: flex;
  align-items: center;
  gap: 5px;
  cursor: pointer;
  transition: all 0.2s;
  
  &:hover {
    background: rgba(0,0,0,0.7);
  }
`;

const FollowButton = styled.button`
  margin-top: 20px;
  padding: 8px 20px;
  background: ${props => props.isFollowing ? '#e74c3c' : '#3498db'};
  color: white;
  border: none;
  border-radius: 20px;
  display: flex;
  align-items: center;
  gap: 5px;
  cursor: pointer;
  transition: all 0.2s;
  
  &:hover {
    background: ${props => props.isFollowing ? '#c0392b' : '#2980b9'};
  }
`;

const Modal = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0,0,0,0.5);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: white;
  padding: 20px;
  border-radius: 8px;
  width: 80%;
  max-width: 600px;
  max-height: 80vh;
  overflow-y: auto;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 10px;
  right: 10px;
  background: none;
  border: none;
  font-size: 20px;
  cursor: pointer;
`;

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

const ProfileHeader = ({ user, isOwnProfile }) => {
  const [isFollowing, setIsFollowing] = useState(false);
  const [followersCount, setFollowersCount] = useState(user.stats?.followersCount || 0);
  const [showFollowersModal, setShowFollowersModal] = useState(false);
  const [showFollowingModal, setShowFollowingModal] = useState(false);
  const [loading, setLoading] = useState(false);

  // Função para construir a URL correta
  const getImageUrl = (imagePath) => {
    if (!imagePath) return '/default-avatar.jpg';
    if (imagePath.startsWith('http')) return imagePath;
    return `${API_BASE_URL}${imagePath}`;
  };

  // Verificar status de follow ao carregar
  useEffect(() => {
    if (!isOwnProfile) {
      checkFollowStatus();
    }
  }, [user._id, isOwnProfile]);

  const checkFollowStatus = async () => {
    try {
      const { data } = await api.get(`/users/${user._id}/follow-status`);
      setIsFollowing(data.isFollowing);
    } catch (error) {
      console.error('Error checking follow status:', error);
    }
  };

  const handleFollow = async () => {
    try {
      setLoading(true);
      if (isFollowing) {
        await api.delete(`/users/${user._id}/unfollow`);
        setFollowersCount(prev => prev - 1);
      } else {
        await api.post(`/users/${user._id}/follow`);
        setFollowersCount(prev => prev + 1);
      }
      setIsFollowing(!isFollowing);
    } catch (error) {
      console.error('Error toggling follow:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <HeaderContainer>
      <CoverPhoto src={getImageUrl(user.profile?.coverPhoto) || '/default-cover.jpg'} />
      <ProfileInfo>
        <Avatar 
          src={getImageUrl(user.profile?.avatar) || '/default-avatar.jpg'} 
          alt={user.username}
          crossOrigin="anonymous"
          onError={(e) => {
            e.target.src = '/default-avatar.jpg';
          }}
        />
        <Username>@{user.username}</Username>
        <Bio>{user.profile?.bio || 'No bio yet'}</Bio>
        
        <StatsContainer>
          <StatItem onClick={() => setShowFollowersModal(true)}>
            <StatNumber>{user.stats?.memeCount || 0}</StatNumber>
            <StatLabel>Posts</StatLabel>
          </StatItem>
          <StatItem onClick={() => setShowFollowersModal(true)}>
            <StatNumber>{followersCount}</StatNumber>
            <StatLabel>Followers</StatLabel>
          </StatItem>
          <StatItem onClick={() => setShowFollowingModal(true)}>
            <StatNumber>{user.stats?.followingCount || 0}</StatNumber>
            <StatLabel>Following</StatLabel>
          </StatItem>
        </StatsContainer>

        {!isOwnProfile && (
          <FollowButton 
            onClick={handleFollow} 
            disabled={loading}
            isFollowing={isFollowing}
          >
            {isFollowing ? (
              <>
                <FiUserCheck /> Following
              </>
            ) : (
              <>
                <FiUserPlus /> Follow
              </>
            )}
          </FollowButton>
        )}
      </ProfileInfo>

      {isOwnProfile && (
        <EditButton>
          <FiEdit /> Edit Profile
        </EditButton>
      )}

      {/* Modals for Followers/Following */}
      {showFollowersModal && (
        <Modal onClick={() => setShowFollowersModal(false)}>
          <ModalContent onClick={e => e.stopPropagation()}>
            <CloseButton onClick={() => setShowFollowersModal(false)}>×</CloseButton>
            <UserFollowers 
              userId={user._id} 
              followersCount={followersCount} 
            />
          </ModalContent>
        </Modal>
      )}

      {showFollowingModal && (
        <Modal onClick={() => setShowFollowingModal(false)}>
          <ModalContent onClick={e => e.stopPropagation()}>
            <CloseButton onClick={() => setShowFollowingModal(false)}>×</CloseButton>
            <UserFollowing 
              userId={user._id} 
              followingCount={user.stats?.followingCount || 0} 
            />
          </ModalContent>
        </Modal>
      )}
    </HeaderContainer>
  );
};

export default ProfileHeader;
