const MemeCard = ({ meme, isRepost = false, onDelete }) => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [isLiked, setIsLiked] = useState(false);
  const [likeCount, setLikeCount] = useState(meme.likes?.length || 0);
  const [showComments, setShowComments] = useState(false);
  const [commentCount, setCommentCount] = useState(meme.commentCount || 0);
  const [reaction, setReaction] = useState(null);
  const [isReposted, setIsReposted] = useState(false);
  const [repostCount, setRepostCount] = useState(meme.repostCount || 0);
  const [isCheckingRepost, setIsCheckingRepost] = useState(true);
  const [showOptions, setShowOptions] = useState(false);
  const [isDeleting, setIsDeleting] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  const [showConfirmDialog, setShowConfirmDialog] = useState(false);
  const [deletionProgress, setDeletionProgress] = useState(0);

  // Estados para o player de v√≠deo customizado
  const [isPlaying, setIsPlaying] = useState(false);
  const [progress, setProgress] = useState(0);
  const [duration, setDuration] = useState(0);
  const [currentTime, setCurrentTime] = useState(0);
  const videoRef = useRef(null);

  useEffect(() => {
    if (user && meme.likes) {
      setIsLiked(meme.likes.some(like => like.toString() === user._id));
    }
    setLikeCount(meme.likes?.length || 0);
    setCommentCount(meme.commentCount || 0);
    setRepostCount(meme.repostCount || 0);

    if (user) {
      checkIfReposted();
    } else {
      setIsCheckingRepost(false);
    }
  }, [meme, user]);


  const handleDelete = async () => {
    setShowConfirmDialog(false);
    setIsDeleting(true);

    try {
      // Barra de progresso simulada (opcional)
      const interval = setInterval(() => {
        setDeletionProgress(prev => {
          if (prev >= 90) {
            clearInterval(interval);
            return prev;
          }
          return prev + 10;
        });
      }, 100);

      const token = localStorage.getItem('token');
      const response = await api.delete(`/memes/${meme._id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      clearInterval(interval);
      setDeletionProgress(100);

      if (!response.data?.success) {
        throw new Error(response.data?.message || 'Falha ao deletar');
      }

      setShowSuccess(true);
      setTimeout(() => {
        if (onDelete) onDelete(meme._id); 
        setShowSuccess(false);
      }, 1500);

    } catch (err) {
      console.error('Erro ao deletar meme:', err);

      if (err.response?.status === 410) {
        // Se o meme j√° foi deletado, apenas remova do frontend
        if (onDelete) onDelete(meme._id);
        alert('Este meme j√° foi deletado anteriormente');
      } else {
        alert(err.response?.data?.message || 'Erro ao deletar meme');
      }
    } finally {
      setIsDeleting(false);
      setDeletionProgress(0);
    }
  };

  // di√°logo de confirma√ß√£o
  const openDeleteConfirmation = () => {
    setShowOptions(false);
    setShowConfirmDialog(true);
  };


  const togglePlay = () => {
    if (videoRef.current) {
      if (isPlaying) {
        videoRef.current.pause();
      } else {
        videoRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const handleTimeUpdate = () => {
    if (videoRef.current) {
      const currentProgress = (videoRef.current.currentTime / videoRef.current.duration) * 100;
      setProgress(currentProgress);
      setCurrentTime(videoRef.current.currentTime);
    }
  };

  const handleLoadedMetadata = () => {
    if (videoRef.current) {
      setDuration(videoRef.current.duration);
    }
  };

  const handleSeek = (e) => {
    if (videoRef.current) {
      const rect = e.target.getBoundingClientRect();
      const pos = (e.clientX - rect.left) / rect.width;
      videoRef.current.currentTime = pos * videoRef.current.duration;
    }
  };

  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = Math.floor(time % 60);
    return `${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
  };

  const checkIfReposted = async () => {
    try {
      if (meme.originalMeme && meme.author._id === user._id) {
        setIsReposted(true);
      } else {
        setIsReposted(meme.reposts?.some(repost => repost.toString() === user._id));
      }
    } catch (error) {
      console.error('Erro ao verificar repost:', error);
    } finally {
      setIsCheckingRepost(false);
    }
  };

  const handleLike = async () => {
    if (!user) return;

    try {
      const response = await api.post(`/memes/${meme._id}/like`);
      setIsLiked(response.data.isLiked);
      setLikeCount(response.data.likeCount);
      setReaction(response.data.isLiked ? 'like' : 'dislike');
      setTimeout(() => setReaction(null), 1500);
    } catch (error) {
      console.error('Erro ao curtir:', error);
    }
  };

  const handleDislike = async () => {
    if (!user || !isLiked) return;

    try {
      const response = await api.post(`/memes/${meme._id}/like`);
      setIsLiked(false);
      setLikeCount(response.data.likeCount);
      setReaction('dislike');
      setTimeout(() => setReaction(null), 1500);
    } catch (error) {
      console.error('Erro ao remover curtida:', error);
    }
  };

  const handleRepost = async () => {
    if (!user) return;

    if (meme.author?._id === user._id) {
      setReaction('self-repost');
      setTimeout(() => setReaction(null), 1500);
      return;
    }

    if (isReposted || isRepost) return;

    try {
      const response = await api.post(`/memes/${meme._id}/repost`);
      setIsReposted(true);
      setRepostCount(response.data.repostCount);

      setReaction('repost');
      setTimeout(() => setReaction(null), 1500);
    } catch (error) {
      console.error('Erro ao repostar:', error);
      if (error.response && error.response.status === 409) {
        setIsReposted(true);
      }
    }
  };

  const handleCommentSubmit = async (formData) => {
    try {
      const response = await api.post(
        `/memes/${meme._id}/comments`,
        formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        }
      );

      const updatedMeme = await api.get(`/memes/${meme._id}`);
      setCommentCount(updatedMeme.data.commentCount || 0);

      return response.data;
    } catch (error) {
      console.error('Erro detalhado:', {
        message: error.message,
        response: error.response?.data,
        status: error.response?.status,
        config: error.config
      });

      throw new Error(error.response?.data?.message || error.message || 'Erro ao enviar coment√°rio');
    }
  };

  const buildUrl = (url) => {
    if (!url) {
      console.warn('URL is empty or undefined');
      return 'https://i.pravatar.cc/150?img=3';
    }

    // Se j√° for uma URL completa ou blob, retorna diretamente
    if (url.startsWith('http') || url.startsWith('blob:')) {
      return url;
    }

    // Normaliza o caminho (adiciona / se necess√°rio)
    const normalizedPath = url.startsWith('/') ? url : `/${url}`;

    // Adiciona o prefixo da API
    const apiUrl = process.env.REACT_APP_API_URL || 'https://api.jokesteronline.org';
    const fullUrl = `${apiUrl}${normalizedPath}`;

    return fullUrl;
  };
  const renderDeleteOverlay = () => {
    if (isDeleting) {
      return (
        <div style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'rgba(0,0,0,0.5)',
          zIndex: 5,
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center'
        }}>
          <div style={{
            width: '80%',
            background: 'rgba(255,255,255,0.2)',
            borderRadius: '10px',
            overflow: 'hidden'
          }}>
            <div style={{
              width: `${deletionProgress}%`,
              height: '6px',
              background: 'var(--primary)',
              transition: 'width 0.3s ease'
            }} />
          </div>
        </div>
      );
    }
    return null;
  };
  return (
    <>
      <Card $deleting={isDeleting}>
        {/* Overlay de dele√ß√£o em progresso */}
        {isDeleting && (
          <div style={{
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: 'rgba(0,0,0,0.5)',
            zIndex: 5,
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center'
          }}>
            <div style={{
              width: '80%',
              background: 'rgba(255,255,255,0.2)',
              borderRadius: '10px',
              overflow: 'hidden'
            }}>
              <div style={{
                width: `${deletionProgress}%`,
                height: '6px',
                background: 'var(--primary)',
                transition: 'width 0.3s ease'
              }} />
            </div>
          </div>
        )}

        {/* Mensagem de sucesso ap√≥s deletar */}
        {showSuccess && (
          <SuccessMessage>
            <FaCheck size={18} />
            <span>Meme deletado com sucesso!</span>
          </SuccessMessage>
        )}

        {/* Menu de op√ß√µes */}
        {user?._id === meme.author?._id && (
          <div style={{ position: 'relative' }}>
            <OptionsButton 
              onClick={(e) => {
                e.stopPropagation();
                setShowOptions(!showOptions);
              }}
            >
              <FaEllipsisH size={14} />
            </OptionsButton>

            {showOptions && (
              <OptionsMenu onClick={(e) => e.stopPropagation()}>
                <OptionItem onClick={openDeleteConfirmation}>
                  <FiTrash2 size={14} />
                  <span>Deletar</span>
                </OptionItem>
              </OptionsMenu>
            )}
          </div>
        )}

        {/* Badge de repost */}
        {isRepost && (
          <RepostBadge>
            <FiRepeat /> Repostado por @{meme.repostedBy?.username || 'usu√°rio'}
          </RepostBadge>
        )}

        {/* Anima√ß√£o de rea√ß√£o */}
        {reaction && (
          <ReactionAnimation style={{
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            color: reaction === 'like' ? 'var(--primary)' : 
                  reaction === 'dislike' ? '#f00' : 
                  reaction === 'repost' ? '#10b981' : 'var(--primary)'
          }}>
            {reaction === 'like' ? <FaSmile /> : 
             reaction === 'dislike' ? <FaAngry /> : 
             reaction === 'repost' ? <FaCheck /> : 
             <span style={{ fontSize: '1rem' }}>Voc√™ √© o autor!</span>}
          </ReactionAnimation>
        )}

        {/* Cabe√ßalho com avatar e nome de usu√°rio */}
        <Header>
          <Avatar 
            src={
              meme.author?.profile?.avatar
                ? buildUrl(meme.author.profile.avatar)
                : 'https://i.pravatar.cc/150?img=3'
            }
            alt={meme.author?.username}
            onClick={() => meme.author && navigate(`/users/${meme.author._id}`)}
            crossOrigin="anonymous"
            onError={(e) => {
              e.target.src = 'https://i.pravatar.cc/150?img=3';
            }}
          />
          <Username 
            onClick={() => meme.author && navigate(`/users/${meme.author._id}`)}
          >
            @{meme.author?.username || 'usu√°rio desconhecido'}
          </Username>
        </Header>

        {/* Container de m√≠dia (imagem ou v√≠deo) */}
        <MediaContainer>
          {meme.mediaType === 'image' ? (
            <ResponsiveImage 
              src={buildUrl(meme.mediaUrl)}
              alt={meme.caption || 'Meme image'}
              style={{
                transform: showComments ? 'scale(0.98)' : 'scale(1)',
                filter: showComments ? 'brightness(0.95)' : 'none'
              }}
              crossOrigin="anonymous"
              onError={(e) => {
                e.target.src = 'https://placehold.co/600x400?text=Imagem+n√£o+carregada';
                e.target.onerror = null;
              }}
            />
          ) : (
            <VideoContainer onClick={togglePlay}>
              <ResponsiveVideo
                ref={videoRef}
                src={buildUrl(meme.mediaUrl)}
                preload="metadata"
                onTimeUpdate={handleTimeUpdate}
                onLoadedMetadata={handleLoadedMetadata}
                onEnded={() => setIsPlaying(false)}
                crossOrigin="anonymous"
              />

              {!isPlaying && (
                <PlayButton onClick={(e) => {
                  e.stopPropagation();
                  togglePlay();
                }} />
              )}

              <VideoControls onClick={(e) => e.stopPropagation()}>
                <ControlButton onClick={togglePlay}>
                  {isPlaying ? '‚ùö‚ùö' : '‚ñ∂'}
                </ControlButton>
                <ProgressBar onClick={handleSeek}>
                  <Progress $progress={`${progress}%`} />
                </ProgressBar>
                <TimeDisplay>
                  {formatTime(currentTime)} / {formatTime(duration)}
                </TimeDisplay>
                <ControlButton onClick={() => {
                  if (videoRef.current) {
                    videoRef.current.muted = !videoRef.current.muted;
                  }
                }}>
                  {videoRef.current?.muted ? 'üîá' : 'üîà'}
                </ControlButton>
              </VideoControls>

              <div style={{
                position: 'absolute',
                top: 0,
                left: 0,
                right: 0,
                bottom: 0,
                background: `linear-gradient(45deg, 
                  rgba(255,255,255,0.1), 
                  rgba(255,69,0,0.05))`,
                pointerEvents: 'none'
              }}/>
            </VideoContainer>
          )}
        </MediaContainer>

        {/* Legenda do meme */}
        {meme.caption && <Caption>{meme.caption}</Caption>}

        {/* Tags do meme */}
        {meme.tags?.length > 0 && (
          <Tags>
            {meme.tags.map(tag => (
              <Tag key={tag} onClick={() => navigate(`/explore?tag=${tag}`)}>#{tag}</Tag>
            ))}
          </Tags>
        )}

        {/* A√ß√µes (like, coment√°rio, repost, etc) */}
        <Actions>
          <ActionGroup>
            <ActionButton 
              onClick={handleLike} 
              $active={isLiked}
              $color="var(--primary)"
            >
              <FaSmile /> {likeCount}
            </ActionButton>
            <ActionButton 
              onClick={handleDislike} 
              $active={!isLiked && likeCount > 0}
              $color="var(--text)"
            >
              <FaAngry /> 
            </ActionButton>
            <ActionButton 
              onClick={() => setShowComments(!showComments)}
              $active={showComments}
              $color="#f000"
            >
              <FiMessageSquare /> {commentCount}
            </ActionButton>
          </ActionGroup>

          <ActionGroup>
            <RepostButton 
              onClick={handleRepost}
              $active={isReposted}
              disabled={isReposted || isCheckingRepost || isRepost || (meme.author?._id === user?._id)}
              title={meme.author?._id === user?._id ? "Voc√™ n√£o pode repostar seu pr√≥prio conte√∫do" : ""}
            >
              {isCheckingRepost ? (
                <FiRepeat />
              ) : isReposted ? (
                <FaCheck />
              ) : (
                <FiRepeat />
              )} {repostCount}
            </RepostButton>
            <ActionButton>
              <FiShare2 />
            </ActionButton>
          </ActionGroup>
        </Actions>

        {/* Se√ß√£o de coment√°rios */}
        {showComments && (
          <CommentSection 
            memeId={meme._id}
            onCommentSubmit={handleCommentSubmit}
          />
        )}
      </Card>

      {/* Di√°logo de confirma√ß√£o de dele√ß√£o */}
      {showConfirmDialog && (
        <ConfirmationDialog>
          <DialogContent>
            <DialogTitle>Tem certeza que deseja deletar este meme?</DialogTitle>
            <p style={{ textAlign: 'center', color: 'var(--text-light)' }}>
              Esta a√ß√£o √© irrevers√≠vel e remover√° o meme permanentemente.
            </p>
            <DialogButtons>
              <DialogButton onClick={handleDelete}>
                <FiCheck size={16} />
                Confirmar
              </DialogButton>
              <DialogButton onClick={() => setShowConfirmDialog(false)}>
                <FiX size={16} />
                Cancelar
              </DialogButton>
            </DialogButtons>
          </DialogContent>
        </ConfirmationDialog>
      )}
    </>
  );
};

export default MemeCard;
