import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import api from '../../services/api';
import UserCard from './UserCard';

const FollowingContainer = styled.div`
  margin-top: 20px;
`;

const UserFollowing = ({ userId, followingCount }) => {
  const [following, setFollowing] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchFollowing = async () => {
      try {
        setLoading(true);
        const { data } = await api.get(`/users/${userId}/following`);
        setFollowing(data.data);
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };

    fetchFollowing();
  }, [userId]);

  if (loading) return <div>Loading following...</div>;
  if (error) return <div>Error loading following</div>;

  return (
    <FollowingContainer>
      <h3>{followingCount} Following</h3>
      {following.map(follow => (
        <UserCard 
          key={follow._id} 
          user={follow.following} 
          showFollowButton={true}
        />
      ))}
    </FollowingContainer>
  );
};

export default UserFollowing;
