import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { FiMessageSquare, FiShare2, FiHeart } from 'react-icons/fi';
import { FaHeart } from 'react-icons/fa';
import api from '../services/api';
import { useAuth } from '../contexts/AuthContext';
import CommentSection from './CommentSection';

const Card = styled.div`
  background: #fff;
  border-radius: 12px;
  margin-bottom: 24px;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
  overflow: hidden;
`;

const Header = styled.div`
  display: flex;
  align-items: center;
  padding: 12px 16px;
`;

const Avatar = styled.img`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  margin-right: 12px;
  object-fit: cover;
`;

const Username = styled.span`
  font-weight: 600;
`;

const MediaContainer = styled.div`
  img, video {
    width: 100%;
    display: block;
  }
`;

const Caption = styled.p`
  padding: 12px 16px;
  margin: 0;
`;

const Tags = styled.div`
  padding: 0 16px 12px;
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
`;

const Tag = styled.span`
  color: #ff4500;
  font-size: 0.9rem;
`;

const Actions = styled.div`
  display: flex;
  padding: 8px 16px;
  border-top: 1px solid #f0f0f0;
`;

const ActionButton = styled.button`
  background: none;
  border: none;
  display: flex;
  align-items: center;
  margin-right: 24px;
  color: ${props => props.$active ? '#ff4500' : '#666'};
  cursor: pointer;
  font-size: 0.9rem;
  
  svg {
    margin-right: 6px;
    font-size: 1.2rem;
  }

  &:hover {
    color: #ff4500;
  }
`;

const MemeCard = ({ meme }) => {
  const { user } = useAuth();
  const [isLiked, setIsLiked] = useState(false);
  const [likeCount, setLikeCount] = useState(meme.likes?.length || 0);
  const [showComments, setShowComments] = useState(false);
  const [commentCount, setCommentCount] = useState(
    (meme.comments?.length || 0) + 
    (meme.comments?.reduce((acc, c) => acc + (c.replies?.length || 0), 0) || 0)
  );

  useEffect(() => {
    if (user && meme.likes) {
      setIsLiked(meme.likes.includes(user._id));
    }
    setLikeCount(meme.likes?.length || 0);
    setCommentCount(meme.commentCount || 0);
  }, [meme, user]);

  const handleLike = async () => {
    if (!user) return;
    
    try {
      const response = await api.post(`/memes/${meme._id}/like`);
      setIsLiked(response.data.isLiked);
      setLikeCount(response.data.likeCount);
    } catch (error) {
      console.error('Erro ao curtir:', error);
    }
  };

  
  const handleCommentSubmit = async (text) => {
    try {
      await api.post(`/memes/${meme._id}/comments`, { text });
      // Atualiza a contagem incluindo respostas
      const updatedMeme = await api.get(`/memes/${meme._id}`);
      setCommentCount(
        (updatedMeme.data.comments?.length || 0) + 
        (updatedMeme.data.comments?.reduce((acc, c) => acc + (c.replies?.length || 0), 0) || 0
      ));
      setShowComments(true);
    } catch (error) {
      if (error.response?.status === 429) {
        alert(error.response.data.message);
      }
      console.error('Erro ao comentar:', error);
    }
  };

  return (
    <Card>
      <Header>
        <Avatar 
          src={meme.author.profile?.avatar || 'https://i.pravatar.cc/150?img=3'} 
          alt={meme.author.username}
        />
        <Username>@{meme.author.username}</Username>
      </Header>
      
      <MediaContainer>
        {meme.mediaType === 'image' ? (
          <img src={meme.mediaUrl} alt={meme.caption} />
        ) : (
          <video src={meme.mediaUrl} controls />
        )}
      </MediaContainer>

      {meme.caption && <Caption>{meme.caption}</Caption>}
      
      {meme.tags?.length > 0 && (
        <Tags>
          {meme.tags.map(tag => (
            <Tag key={tag}>#{tag}</Tag>
          ))}
        </Tags>
      )}

      <Actions>
        <ActionButton 
          onClick={handleLike} 
          $active={isLiked}
        >
          {isLiked ? <FaHeart /> : <FiHeart />} {likeCount}
        </ActionButton>
        <ActionButton 
          onClick={() => setShowComments(!showComments)}
          $active={showComments}
        >
          <FiMessageSquare /> {commentCount}
        </ActionButton>
        <ActionButton>
          <FiShare2 /> Compartilhar
        </ActionButton>
      </Actions>

      {showComments && (
        <CommentSection 
          memeId={meme._id}
          onCommentSubmit={handleCommentSubmit}
        />
      )}
    </Card>
  );
};

export default MemeCard;
