import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import api from '../services/api';
import { useAuth } from '../contexts/AuthContext';
import { 
  FiSend, 
  FiCornerDownLeft, 
  FiThumbsUp, 
  FiThumbsDown,
  FiMoreHorizontal
} from 'react-icons/fi';
import { FaThumbsUp, FaThumbsDown } from 'react-icons/fa';

const CommentContainer = styled.div`
  padding: 16px;
  border-top: 1px solid #f0f0f0;
  background: #fff;
  border-radius: 0 0 12px 12px;
`;

const CommentList = styled.div`
  max-height: 400px;
  overflow-y: auto;
  margin-bottom: 16px;
  padding-right: 8px;

  /* Custom scrollbar */
  &::-webkit-scrollbar {
    width: 6px;
  }
  &::-webkit-scrollbar-track {
    background: #f1f1f1;
    border-radius: 10px;
  }
  &::-webkit-scrollbar-thumb {
    background: #ccc;
    border-radius: 10px;
  }
`;

const CommentItem = styled.div`
  display: flex;
  margin-bottom: 16px;
  position: relative;
  transition: all 0.2s;

  &:hover {
    transform: translateX(2px);
  }
`;

const CommentAvatar = styled.img`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  margin-right: 12px;
  object-fit: cover;
  border: 2px solid #f0f0f0;
  transition: all 0.2s;

  &:hover {
    border-color: #ff4500;
  }
`;

const CommentContent = styled.div`
  flex: 1;
  background: #f9f9f9;
  padding: 12px;
  border-radius: 12px;
  position: relative;
`;

const CommentUser = styled.div`
  font-weight: 600;
  font-size: 0.95rem;
  color: #333;
  display: flex;
  align-items: center;
  gap: 6px;
`;

const UserBadge = styled.span`
  font-size: 0.7rem;
  background: #ff4500;
  color: white;
  padding: 2px 6px;
  border-radius: 12px;
`;

const CommentText = styled.p`
  margin: 8px 0 0;
  font-size: 0.95rem;
  line-height: 1.4;
  color: #333;
`;

const CommentTime = styled.small`
  display: block;
  margin-top: 4px;
  font-size: 0.75rem;
  color: #999;
`;

const CommentForm = styled.form`
  display: flex;
  align-items: center;
  margin-top: 16px;
  background: #fff;
  border-radius: 24px;
  padding: 4px;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
`;

const CommentInput = styled.input`
  flex: 1;
  padding: 12px 16px;
  border: none;
  border-radius: 24px;
  font-size: 0.95rem;
  outline: none;
  background: #f9f9f9;

  &:focus {
    background: #f0f0f0;
  }
`;

const SubmitButton = styled.button`
  background: #ff4500;
  color: white;
  border: none;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  margin-left: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: all 0.2s;

  &:hover {
    background: #e03d00;
    transform: scale(1.05);
  }

  &:active {
    transform: scale(0.95);
  }
`;

const ReplyButton = styled.button`
  background: none;
  border: none;
  color: #666;
  font-size: 0.85rem;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 6px;
  margin-top: 8px;
  padding: 4px 8px;
  border-radius: 16px;
  transition: all 0.2s;

  &:hover {
    color: #ff4500;
    background: rgba(255, 69, 0, 0.1);
  }
`;

const ReplyForm = styled.form`
  display: flex;
  margin: 8px 0 16px 52px;
  background: #fff;
  border-radius: 24px;
  padding: 4px;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
`;

const ReactionButtons = styled.div`
  display: flex;
  gap: 12px;
  margin-top: 12px;
`;

const ReactionButton = styled.button`
  display: flex;
  align-items: center;
  gap: 6px;
  background: none;
  border: none;
  color: ${props => props.$active ? '#ff4500' : '#666'};
  cursor: pointer;
  font-size: 0.85rem;
  padding: 4px 8px;
  border-radius: 16px;
  transition: all 0.2s;
  
  &:hover {
    background: rgba(255, 69, 0, 0.1);
    color: #ff4500;
  }
`;

const MoreOptionsButton = styled.button`
  position: absolute;
  top: 12px;
  right: 12px;
  background: none;
  border: none;
  color: #999;
  cursor: pointer;
  transition: all 0.2s;

  &:hover {
    color: #ff4500;
  }
`;

const CommentSection = ({ memeId, onCommentSubmit }) => {
  const { user } = useAuth();
  const [comments, setComments] = useState([]);
  const [commentText, setCommentText] = useState('');
  const [loading, setLoading] = useState(true);
  const [replyingTo, setReplyingTo] = useState(null);
  const [replyText, setReplyText] = useState('');

  useEffect(() => {
    const fetchComments = async () => {
      try {
        const response = await api.get(`/memes/${memeId}/comments`);
        setComments(response.data.comments);
      } catch (error) {
        console.error('Erro ao buscar comentários:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchComments();
  }, [memeId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!commentText.trim()) return;

    try {
      await onCommentSubmit(commentText);
      setCommentText('');
      const response = await api.get(`/memes/${memeId}/comments`);
      setComments(response.data.comments);
    } catch (error) {
      console.error('Erro ao enviar comentário:', error);
    }
  };

  const handleReplySubmit = async (e, commentId) => {
    e.preventDefault();
    if (!replyText.trim()) return;

    try {
      const response = await api.post(`/memes/${memeId}/comments/${commentId}/replies`, { 
        text: replyText 
      });
      
      const updatedComments = comments.map(comment => {
        if (comment._id === commentId) {
          return {
            ...comment,
            replies: [...(comment.replies || []), response.data.reply]
          };
        }
        return comment;
      });
      
      setComments(updatedComments);
      setReplyText('');
      setReplyingTo(null);
    } catch (error) {
      console.error('Erro ao enviar resposta:', error);
    }
  };

  const handleCommentReaction = async (commentId, reaction) => {
    try {
      const response = await api.post(`/memes/${memeId}/comments/${commentId}/reaction`, { reaction });
      
      setComments(prev => prev.map(comment => {
        if (comment._id === commentId) {
          return {
            ...comment,
            likes: response.data.likes,
            dislikes: response.data.dislikes,
            userReaction: response.data.userReaction
          };
        }
        return comment;
      }));
    } catch (error) {
      console.error('Erro ao reagir:', error);
    }
  };

  const handleReplyReaction = async (commentId, replyId, reaction) => {
    try {
      const response = await api.post(
        `/memes/${memeId}/comments/${commentId}/replies/${replyId}/reaction`, 
        { reaction }
      );
      
      setComments(prev => prev.map(comment => {
        if (comment._id === commentId) {
          const updatedReplies = comment.replies?.map(reply => {
            if (reply._id === replyId) {
              return {
                ...reply,
                likes: response.data.likes,
                dislikes: response.data.dislikes,
                userReaction: response.data.userReaction
              };
            }
            return reply;
          });
          
          return { ...comment, replies: updatedReplies || [] };
        }
        return comment;
      }));
    } catch (error) {
      console.error('Erro ao reagir:', error);
    }
  };

  const formatTime = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  return (
    <CommentContainer>
      <CommentList>
        {loading ? (
          <div style={{ textAlign: 'center', padding: '20px', color: '#666' }}>
            Carregando comentários...
          </div>
        ) : comments.length === 0 ? (
          <div style={{ textAlign: 'center', padding: '20px', color: '#666' }}>
            Nenhum comentário ainda. Seja o primeiro!
          </div>
        ) : (
          comments.map(comment => (
            <React.Fragment key={comment._id}>
              <CommentItem>
                <CommentAvatar 
                  src={comment.user.profile?.avatar || 'https://i.pravatar.cc/150?img=3'} 
                  alt={comment.user.username}
                />
                <CommentContent>
                  <CommentUser>
                    @{comment.user.username}
                    {comment.user.isVerified && <UserBadge>Verificado</UserBadge>}
                    <MoreOptionsButton>
                      <FiMoreHorizontal />
                    </MoreOptionsButton>
                  </CommentUser>
                  <CommentText>{comment.text}</CommentText>
                  <CommentTime>{formatTime(comment.createdAt)}</CommentTime>
                  
                  <ReactionButtons>
                    <ReactionButton 
                      onClick={() => handleCommentReaction(comment._id, 'like')}
                      $active={comment.userReaction === 'like'}
                    >
                      {comment.userReaction === 'like' ? <FaThumbsUp /> : <FiThumbsUp />}
                      {comment.likes?.length || 0}
                    </ReactionButton>
                    <ReactionButton 
                      onClick={() => handleCommentReaction(comment._id, 'dislike')}
                      $active={comment.userReaction === 'dislike'}
                    >
                      {comment.userReaction === 'dislike' ? <FaThumbsDown /> : <FiThumbsDown />}
                      {comment.dislikes?.length || 0}
                    </ReactionButton>
                  </ReactionButtons>
                  
                  {user && (
                    <ReplyButton onClick={() => setReplyingTo(replyingTo === comment._id ? null : comment._id)}>
                      <FiCornerDownLeft size={14} />
                      Responder
                    </ReplyButton>
                  )}
                </CommentContent>
              </CommentItem>

              {/* Replies */}
              {comment.replies?.map(reply => (
                <CommentItem key={reply._id} style={{ marginLeft: '52px' }}>
                  <CommentAvatar 
                    src={reply.user.profile?.avatar || 'https://i.pravatar.cc/150?img=3'} 
                    alt={reply.user.username}
                    style={{ width: '32px', height: '32px' }}
                  />
                  <CommentContent>
                    <CommentUser>
                      @{reply.user.username}
                      {reply.user.isVerified && <UserBadge>Verificado</UserBadge>}
                    </CommentUser>
                    <CommentText>{reply.text}</CommentText>
                    <CommentTime>{formatTime(reply.createdAt)}</CommentTime>
                    
                    <ReactionButtons>
                      <ReactionButton 
                        onClick={() => handleReplyReaction(comment._id, reply._id, 'like')}
                        $active={reply.userReaction === 'like'}
                      >
                        {reply.userReaction === 'like' ? <FaThumbsUp /> : <FiThumbsUp />}
                        {reply.likes?.length || 0}
                      </ReactionButton>
                      <ReactionButton 
                        onClick={() => handleReplyReaction(comment._id, reply._id, 'dislike')}
                        $active={reply.userReaction === 'dislike'}
                      >
                        {reply.userReaction === 'dislike' ? <FaThumbsDown /> : <FiThumbsDown />}
                        {reply.dislikes?.length || 0}
                      </ReactionButton>
                    </ReactionButtons>
                  </CommentContent>
                </CommentItem>
              ))}

              {/* Reply Form */}
              {replyingTo === comment._id && (
                <ReplyForm onSubmit={(e) => handleReplySubmit(e, comment._id)}>
                  <CommentInput
                    type="text"
                    value={replyText}
                    onChange={(e) => setReplyText(e.target.value)}
                    placeholder="Escreva uma resposta..."
                    autoFocus
                  />
                  <SubmitButton type="submit">
                    <FiSend size={18} />
                  </SubmitButton>
                </ReplyForm>
              )}
            </React.Fragment>
          ))
        )}
      </CommentList>

      {user && (
        <CommentForm onSubmit={handleSubmit}>
          <CommentInput
            type="text"
            value={commentText}
            onChange={(e) => setCommentText(e.target.value)}
            placeholder="Adicione um comentário..."
          />
          <SubmitButton type="submit">
            <FiSend size={18} />
          </SubmitButton>
        </CommentForm>
      )}
    </CommentContainer>
  );
};

export default CommentSection;
