import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { FiMessageSquare, FiShare2, FiHeart } from 'react-icons/fi';
import { FaHeart } from 'react-icons/fa';
import api from '../services/api';
import { useAuth } from '../contexts/AuthContext';
import CommentSection from './CommentSection';


const Card = styled.div`
  background: #fff;
  border-radius: 12px;
  margin-bottom: 24px;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
  overflow: hidden;
`;

const Header = styled.div`
  display: flex;
  align-items: center;
  padding: 12px 16px;
`;

const Avatar = styled.img`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  margin-right: 12px;
  object-fit: cover;
`;

const Username = styled.span`
  font-weight: 600;
`;

const MediaContainer = styled.div`
  width: 100%;
  min-height: 300px;
  background: #f5f5f5;
  display: flex;
  justify-content: center;
  align-items: center;
  
  img, video {
    max-width: 100%;
    max-height: 500px;
    display: block;
    object-fit: contain;
  }
`;

const Caption = styled.p`
  padding: 12px 16px;
  margin: 0;
`;

const Tags = styled.div`
  padding: 0 16px 12px;
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
`;

const Tag = styled.span`
  color: #ff4500;
  font-size: 0.9rem;
`;

const Actions = styled.div`
  display: flex;
  padding: 8px 16px;
  border-top: 1px solid #f0f0f0;
`;

const ActionButton = styled.button`
  background: none;
  border: none;
  display: flex;
  align-items: center;
  margin-right: 24px;
  color: ${props => props.$active ? '#ff4500' : '#666'};
  cursor: pointer;
  font-size: 0.9rem;
  
  svg {
    margin-right: 6px;
    font-size: 1.2rem;
  }

  &:hover {
    color: #ff4500;
  }
`;
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';
const MemeCard = ({ meme }) => {
  const { user } = useAuth();
  const [isLiked, setIsLiked] = useState(false);
  const [likeCount, setLikeCount] = useState(meme.likes?.length || 0);
  const [showComments, setShowComments] = useState(false);
  const [commentCount, setCommentCount] = useState(
    (meme.comments?.length || 0) + 
    (meme.comments?.reduce((acc, c) => acc + (c.replies?.length || 0), 0) || 0)
  );

  useEffect(() => {
    if (user && meme.likes) {
      setIsLiked(meme.likes.includes(user._id));
    }
    setLikeCount(meme.likes?.length || 0);
    setCommentCount(meme.commentCount || 0);
  }, [meme, user]);

  const handleLike = async () => {
    if (!user) return;
    
    try {
      const response = await api.post(`/memes/${meme._id}/like`);
      setIsLiked(response.data.isLiked);
      setLikeCount(response.data.likeCount);
    } catch (error) {
      console.error('Erro ao curtir:', error);
    }
  };

  
  const handleCommentSubmit = async (text) => {
    try {
      const response = await api.post(`/memes/${meme._id}/comments`, { text });
      
      // Atualiza a contagem de comentários buscando novamente
      const updatedMeme = await api.get(`/memes/${meme._id}`);
      const comments = await api.get(`/memes/${meme._id}/comments`);
      
      // Calcula o total de comentários + respostas
      const countReplies = (commentsArray) => {
        return commentsArray.reduce((total, comment) => {
          return total + 1 + (comment.replies ? countReplies(comment.replies) : 0);
        }, 0);
      };
      
      setCommentCount(countReplies(comments.data));
      setShowComments(true);
      
      return response.data;
    } catch (error) {
      console.error('Erro ao comentar:', error);
      throw error;
    }
  };
  return (
    <Card>
      <Header>
        <Avatar 
          src={
              user.profile?.avatar
                ? user.profile.avatar.startsWith('http')
                  ? user.profile.avatar
                  : `${process.env.REACT_APP_API_URL || 'http://localhost:5000'}${user.profile.avatar}`
                : 'https://i.pravatar.cc/150?img=3'
            }
            alt={user.username}
            crossOrigin="anonymous"
            onError={(e) => {
              e.target.src = 'https://i.pravatar.cc/150?img=3';
              e.target.onerror = null; // Previne loop de erro
            }}
          />
        <Username 
          onClick={() => navigate(`/profile/${meme.author._id}`)}
          style={{ cursor: 'pointer' }}
        >
          @{meme.author.username}
        </Username>
      </Header>
      
      <MediaContainer>
        {meme.mediaType === 'image' ? (
          <img 
            src={meme.mediaUrl.startsWith('http') ? meme.mediaUrl : `${API_BASE_URL}${meme.mediaUrl}`}
            alt={meme.caption || 'Meme image'}
            crossOrigin="anonymous"
            onError={(e) => {
              e.target.src = 'https://placehold.co/600x400?text=Imagem+não+carregada';
              e.target.onerror = null;
            }}
          />
        ) : (
          <video 
            src={meme.mediaUrl.startsWith('http') ? meme.mediaUrl : `${API_BASE_URL}${meme.mediaUrl}`} 
            controls 
            crossOrigin="anonymous"
          />
        )}
      </MediaContainer>

      {meme.caption && <Caption>{meme.caption}</Caption>}
      
      {meme.tags?.length > 0 && (
        <Tags>
          {meme.tags.map(tag => (
            <Tag key={tag}>#{tag}</Tag>
          ))}
        </Tags>
      )}

      <Actions>
        <ActionButton 
          onClick={handleLike} 
          $active={isLiked}
        >
          {isLiked ? <FaHeart /> : <FiHeart />} {likeCount}
        </ActionButton>
        <ActionButton 
          onClick={() => setShowComments(!showComments)}
          $active={showComments}
        >
          <FiMessageSquare /> {commentCount}
        </ActionButton>
        <ActionButton>
          <FiShare2 /> Compartilhar
        </ActionButton>
      </Actions>

      {showComments && (
        <CommentSection 
          memeId={meme._id}
          onCommentSubmit={handleCommentSubmit}
        />
      )}
    </Card>
  );
};

export default MemeCard;
