// src/components/profile/UserMemes.jsx
import { useState, useEffect } from 'react';
import styled from 'styled-components';
import api from '../../services/api';
import MemeThumbnail from '../MemeThumbnail';

const MemeGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
  gap: 20px;
  margin-top: 20px;
`;

const UserMemes = ({ userId }) => {
  const [memes, setMemes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMemes = async () => {
      try {
        const { data } = await api.get(`/users/${userId}/memes`);
        setMemes(data);
      } catch (error) {
        console.error('Error fetching user memes:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchMemes();
  }, [userId]);

  if (loading) return <div>Loading memes...</div>;

  return (
    <MemeGrid>
      {memes.map(meme => (
        <MemeThumbnail key={meme._id} meme={meme} />
      ))}
    </MemeGrid>
  );
};

export default UserMemes;
