import React, { useState, useEffect } from 'react';
import styled, { keyframes, css } from 'styled-components';
import { FiMessageSquare, FiRepeat } from 'react-icons/fi';
import { FaHeart, FaRegHeart } from 'react-icons/fa';
import api from '../../services/api';
import { useAuth } from '../../contexts/AuthContext';
import CommentSection from '../CommentSection';
import { useNavigate, useParams } from 'react-router-dom';

// Animações premium
const pulse = keyframes`
  0% { transform: scale(1); opacity: 1; }
  50% { transform: scale(1.1); opacity: 0.8; }
  100% { transform: scale(1); opacity: 1; }
`;

const float = keyframes`
  0% { transform: translateY(0px); }
  50% { transform: translateY(-5px); }
  100% { transform: translateY(0px); }
`;

const glow = keyframes`
  0% { box-shadow: 0 0 5px rgba(255, 69, 0, 0.5); }
  50% { box-shadow: 0 0 20px rgba(255, 69, 0, 0.8); }
  100% { box-shadow: 0 0 5px rgba(255, 69, 0, 0.5); }
`;

// Estilos premium
const PremiumCard = styled.div`
  background: #ffffff;
  border-radius: 20px;
  margin-bottom: 30px;
  box-shadow: 0 8px 30px rgba(0, 0, 0, 0.08);
  overflow: hidden;
  transition: all 0.3s cubic-bezier(0.25, 0.8, 0.25, 1);
  position: relative;
  
  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 12px 35px rgba(0, 0, 0, 0.12);
  }

  ${props => props.$isOwner && css`
    border-left: 5px solid #ff4500;
  `}
`;

const PremiumHeader = styled.div`
  display: flex;
  align-items: center;
  padding: 18px 22px;
  position: relative;
`;

const PremiumAvatar = styled.div`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  margin-right: 15px;
  background: ${props => props.$gradient || 'linear-gradient(135deg, #ff4500, #ff8c00)'};
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-weight: bold;
  font-size: 1.2rem;
  cursor: pointer;
  transition: all 0.3s;
  overflow: hidden;
  border: 3px solid white;
  box-shadow: 0 3px 10px rgba(0,0,0,0.1);

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  &:hover {
    transform: scale(1.1);
    animation: ${float} 2s ease-in-out infinite;
  }
`;

const PremiumUsername = styled.span`
  font-weight: 700;
  font-size: 1.1rem;
  color: #333;
  transition: all 0.2s;
  cursor: pointer;

  &:hover {
    color: #ff4500;
    text-decoration: underline;
  }
`;

const PremiumMediaContainer = styled.div`
  width: 100%;
  min-height: 350px;
  background: linear-gradient(to bottom, #f9f9f9, #f0f0f0);
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
  overflow: hidden;
  
  img, video {
    max-width: 100%;
    max-height: 550px;
    display: block;
    object-fit: contain;
    border-radius: 5px;
    transition: transform 0.3s;
  }

  &:hover img, &:hover video {
    transform: scale(1.02);
  }
`;

const PremiumCaption = styled.p`
  padding: 18px 22px;
  margin: 0;
  font-size: 1rem;
  line-height: 1.5;
  color: #444;
  border-bottom: ${props => props.$hasTags ? '1px solid #f0f0f0' : 'none'};
`;

const PremiumTags = styled.div`
  padding: 15px 22px;
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
`;

const PremiumTag = styled.span`
  color: #ff4500;
  font-size: 0.85rem;
  background: rgba(255, 69, 0, 0.1);
  padding: 6px 12px;
  border-radius: 20px;
  transition: all 0.2s;

  &:hover {
    background: rgba(255, 69, 0, 0.2);
    transform: translateY(-2px);
  }
`;

const PremiumActions = styled.div`
  display: flex;
  padding: 12px 22px;
  border-top: 1px solid #f0f0f0;
  background: #fafafa;
`;

const PremiumActionButton = styled.button`
  background: none;
  border: none;
  display: flex;
  align-items: center;
  margin-right: 25px;
  color: ${props => props.$active ? '#ff4500' : '#666'};
  cursor: pointer;
  font-size: 0.95rem;
  transition: all 0.2s;
  position: relative;
  
  svg {
    margin-right: 8px;
    font-size: 1.4rem;
    transition: all 0.2s;
  }

  .count {
    font-weight: ${props => props.$active ? 'bold' : 'normal'};
  }

  &:hover {
    color: #ff4500;
    transform: translateY(-2px);

    svg {
      transform: scale(1.1);
    }
  }

  ${props => props.$disabled && css`
    opacity: 0.5;
    pointer-events: none;
  `}
`;

const RepostBadge = styled.div`
  position: absolute;
  top: 15px;
  right: 15px;
  background: rgba(255, 69, 0, 0.9);
  color: white;
  padding: 5px 10px;
  border-radius: 12px;
  font-size: 0.8rem;
  font-weight: bold;
  display: flex;
  align-items: center;
  z-index: 1;

  svg {
    margin-right: 5px;
  }
`;

const MemeCard = ({ meme }) => {
  if (!meme) {
    return (
      <PremiumCard>
        <div style={{ padding: '20px', color: '#999', textAlign: 'center' }}>
          Conteúdo não disponível
        </div>
      </PremiumCard>
    );
  }
  const navigate = useNavigate();
  const { userId } = useParams();
  const { user } = useAuth();
  const [reaction, setReaction] = useState(null);
  const [likeCount, setLikeCount] = useState(meme?.likes?.length || 0);
  const [showComments, setShowComments] = useState(false);
  const [commentCount, setCommentCount] = useState(meme.commentCount || 0);
  const [isReposted, setIsReposted] = useState(false);
  const [repostCount, setRepostCount] = useState(meme.repostCount || 0);
  const [animateLike, setAnimateLike] = useState(false);
  const [isOwner, setIsOwner] = useState(false);
  const [memes, setMemes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkOwnership = () => {
      setIsOwner(user && meme.author && user._id === meme.author._id);
    };

    const fetchUserMemes = async () => {
      try {
        if (userId) {
          const { data } = await api.get(`/users/${userId}/memes?includeReposts=true`);
          setMemes(data);
        }
      } catch (error) {
        console.error('Error fetching user memes:', error);
      } finally {
        setLoading(false);
      }
    };

    checkOwnership();
    fetchUserMemes();

    if (user && meme.likes) {
      setReaction(meme.likes.some(like => like.toString() === user._id) ? 'like' : null);
    }
    setLikeCount(meme.likes?.length || 0);
    setCommentCount(meme.commentCount || 0);
    setRepostCount(meme.repostCount || 0);
    checkIfReposted();
  }, [meme, user, userId]);

  const checkIfReposted = async () => {
    try {
      const response = await api.get(`/memes/${meme._id}/reposted-by-me`);
      setIsReposted(response.data.isReposted);
    } catch (error) {
      console.error('Erro ao verificar repost:', error);
    }
  };

  const handleReaction = async () => {
    if (!user) return;
    
    try {
      if (reaction === 'like') {
        await api.post(`/memes/${meme._id}/unlike`);
        setReaction(null);
        setLikeCount(prev => prev - 1);
      } else {
        await api.post(`/memes/${meme._id}/like`);
        setReaction('like');
        setLikeCount(prev => prev + 1);
      }
      setAnimateLike(true);
      setTimeout(() => setAnimateLike(false), 600);
    } catch (error) {
      console.error('Erro ao curtir:', error);
    }
  };

  const handleRepost = async () => {
    if (!user || isOwner) return;
    
    try {
      const response = await api.post(`/memes/${meme._id}/repost`);
      setIsReposted(response.data.isReposted);
      setRepostCount(response.data.repostCount);
    } catch (error) {
      console.error('Erro ao repostar:', error);
    }
  };

  const handleCommentSubmit = async (text) => {
    try {
      await api.post(`/memes/${meme._id}/comments`, { text });
      const updatedMeme = await api.get(`/memes/${meme._id}`);
      setCommentCount(updatedMeme.data.commentCount || 0);
      setShowComments(true);
    } catch (error) {
      console.error('Erro ao comentar:', error);
      throw error;
    }
  };

  const getAvatarContent = () => {
    if (meme.author?.profile?.avatar) {
      return <img src={`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}${meme.author.profile.avatar}`} 
             alt={meme.author.username} 
             onError={(e) => {
               e.target.src = '';
               e.target.onerror = null;
             }} />;
    }
    return meme.author?.username?.charAt(0).toUpperCase() || 'U';
  };

  return (
    <PremiumCard $isOwner={isOwner}>
      {meme.isRepost && (
        <RepostBadge>
          <FiRepeat /> Repostado por @{meme.repostedBy?.username || 'usuário'}
        </RepostBadge>
      )}
      
      <PremiumHeader>
        <PremiumAvatar 
          onClick={() => meme.author && navigate(`/users/${meme.author._id}`)}
          $gradient={isOwner ? 'linear-gradient(135deg, #ff4500, #ff8c00)' : 'linear-gradient(135deg, #4e54c8, #8f94fb)'}
        >
          {getAvatarContent()}
        </PremiumAvatar>
        <PremiumUsername onClick={() => meme.author && navigate(`/users/${meme.author._id}`)}>
          @{meme.author?.username || 'usuário desconhecido'}
        </PremiumUsername>
      </PremiumHeader>
      
      <PremiumMediaContainer>
        {meme.mediaType === 'image' ? (
          <img 
            src={`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}${meme.mediaUrl}`}
            alt={meme.caption || 'Meme image'}
            onError={(e) => {
              e.target.src = 'https://placehold.co/600x400?text=Imagem+não+carregada';
              e.target.onerror = null;
            }}
          />
        ) : (
          <video 
            src={`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}${meme.mediaUrl}`}
            controls 
          />
        )}
      </PremiumMediaContainer>

      {meme.caption && <PremiumCaption $hasTags={meme.tags?.length > 0}>{meme.caption}</PremiumCaption>}
      
      {meme.tags?.length > 0 && (
        <PremiumTags>
          {meme.tags.map(tag => (
            <PremiumTag key={tag}>#{tag}</PremiumTag>
          ))}
        </PremiumTags>
      )}

      <PremiumActions>
        <PremiumActionButton 
          onClick={handleReaction}
          $active={reaction === 'like'}
          style={{ animation: animateLike ? `${pulse} 0.6s ease` : 'none' }}
        >
          {reaction === 'like' ? <FaHeart /> : <FaRegHeart />}
          <span className="count">{likeCount}</span>
        </PremiumActionButton>
        
        <PremiumActionButton 
          onClick={() => setShowComments(!showComments)}
          $active={showComments}
        >
          <FiMessageSquare />
          <span className="count">{commentCount}</span>
        </PremiumActionButton>
        
        <PremiumActionButton
          onClick={handleRepost}
          $active={isReposted}
          $disabled={isOwner}
          title={isOwner ? "Você não pode repostar seu próprio meme" : ""}
        >
          <FiRepeat />
          <span className="count">{repostCount}</span>
        </PremiumActionButton>
      </PremiumActions>

      {showComments && (
        <CommentSection 
          memeId={meme._id}
          onCommentSubmit={handleCommentSubmit}
        />
      )}
    </PremiumCard>
  );
};

export default MemeCard;
