import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import { FiMessageSquare, FiShare2, FiRepeat } from 'react-icons/fi';
import api from '../services/api';
import { useAuth } from '../contexts/AuthContext';
import CommentSection from './CommentSection';
import { useNavigate } from 'react-router-dom';
import PropTypes from 'prop-types';

// Animações
const bounce = keyframes`
  0%, 100% { transform: scale(1); }
  50% { transform: scale(1.3); }
`;

const shake = keyframes`
  0% { transform: rotate(0deg); }
  25% { transform: rotate(-10deg); }
  50% { transform: rotate(10deg); }
  75% { transform: rotate(-10deg); }
  100% { transform: rotate(0deg); }
`;

// Estilos
const Card = styled.div`
  background: #fff;
  border-radius: 12px;
  margin-bottom: 24px;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
  overflow: hidden;
`;

const Header = styled.div`
  display: flex;
  align-items: center;
  padding: 12px 16px;
`;

const Avatar = styled.img`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  margin-right: 12px;
  object-fit: cover;
`;

const Username = styled.span`
  font-weight: 600;
  &:hover {
    text-decoration: underline;
    color: #ff4500;
  }
`;

const MediaContainer = styled.div`
  width: 100%;
  min-height: 300px;
  background: #f5f5f5;
  display: flex;
  justify-content: center;
  align-items: center;
  
  img, video {
    max-width: 100%;
    max-height: 500px;
    display: block;
    object-fit: contain;
  }
`;

const Caption = styled.p`
  padding: 12px 16px;
  margin: 0;
`;

const Tags = styled.div`
  padding: 0 16px 12px;
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
`;

const Tag = styled.span`
  color: #ff4500;
  font-size: 0.9rem;
`;

const Actions = styled.div`
  display: flex;
  padding: 8px 16px;
  border-top: 1px solid #f0f0f0;
`;

const ActionButton = styled.button`
  background: none;
  border: none;
  display: flex;
  align-items: center;
  margin-right: 24px;
  color: ${props => props.$active ? '#ff4500' : '#666'};
  cursor: pointer;
  font-size: 0.9rem;
  
  svg {
    margin-right: 6px;
    font-size: 1.2rem;
  }

  &:hover {
    color: #ff4500;
  }
`;

const EmojiButton = styled.button`
  background: none;
  border: none;
  cursor: pointer;
  font-size: 1.5rem;
  margin-right: 24px;
  padding: 0;
  display: flex;
  align-items: center;
  
  span {
    margin-right: 6px;
    display: inline-block;
    animation: ${props => props.$animate ? bounce : 'none'} 0.5s ease;
  }

  &:hover span {
    animation: ${props => props.$animate ? bounce : shake} 0.5s ease;
  }
`;

const MemeCard = ({ meme }) => {
  // Verificação inicial de segurança
  if (!meme || !meme._id) {
    return (
      <PremiumCard>
        <div style={{ padding: '20px', color: '#999', textAlign: 'center' }}>
          Conteúdo não disponível
        </div>
      </PremiumCard>
    );
  }

  const navigate = useNavigate();
  const { user } = useAuth();
  const [reaction, setReaction] = useState(null);
  const [likeCount, setLikeCount] = useState(meme?.likes?.length || 0);
  const [dislikeCount, setDislikeCount] = useState(meme?.dislikes?.length || 0);
  const [showComments, setShowComments] = useState(false);
  const [commentCount, setCommentCount] = useState(meme?.commentCount || 0);
  const [isReposted, setIsReposted] = useState(false);
  const [repostCount, setRepostCount] = useState(meme?.repostCount || 0);
  const [animateLike, setAnimateLike] = useState(false);
  const [animateDislike, setAnimateDislike] = useState(false);

  useEffect(() => {
    if (user && meme?.likes && meme?.dislikes) {
      const userLiked = meme.likes.some(like => like.toString() === user._id);
      const userDisliked = meme.dislikes.some(dislike => dislike.toString() === user._id);
      setReaction(userLiked ? 'like' : userDisliked ? 'dislike' : null);
    }
    setLikeCount(meme?.likes?.length || 0);
    setDislikeCount(meme?.dislikes?.length || 0);
    setCommentCount(meme?.commentCount || 0);
    setRepostCount(meme?.repostCount || 0);
  }, [meme, user]);

  const checkIfReposted = async () => {
    try {
      const response = await api.get(`/memes/${meme._id}/repost-status`);
      setIsReposted(response.data.isReposted);
    } catch (error) {
      console.error('Erro ao verificar repost:', error);
    }
  };

  const handleReaction = async (type) => {
    if (!user) return;
    
    try {
      // Verifica se a rota existe - pode ser /like ou /reaction
      let endpoint = `/memes/${meme._id}/like`;
      
      // Tenta primeiro o endpoint /like
      let response;
      try {
        if (reaction === type) {
          // Remove a reação
          response = await api.delete(endpoint);
        } else {
          // Adiciona/atualiza a reação
          response = await api.post(endpoint, { type });
        }
      } catch (apiError) {
        // Se falhar, tenta o endpoint /reaction
        endpoint = `/memes/${meme._id}/reaction`;
        if (reaction === type) {
          response = await api.post(endpoint, { type: 'remove' });
        } else {
          response = await api.post(endpoint, { type });
        }
      }

      // Atualiza os estados com a resposta
      setReaction(response.data.userReaction || null);
      setLikeCount(response.data.likes || 0);
      setDislikeCount(response.data.dislikes || 0);

      // Trigger animations
      if (type === 'like') {
        setAnimateLike(true);
        setTimeout(() => setAnimateLike(false), 500);
      } else {
        setAnimateDislike(true);
        setTimeout(() => setAnimateDislike(false), 500);
      }
    } catch (error) {
      console.error('Erro ao reagir:', error);
      alert('Ocorreu um erro ao processar sua reação. Por favor, tente novamente.');
    }
  };

  const handleRepost = async () => {
    if (!user) return;
    
    try {
      const response = await api.post(`/memes/${meme._id}/repost`);
      setIsReposted(response.data.isReposted);
      setRepostCount(response.data.repostCount || 0);
    } catch (error) {
      console.error('Erro ao repostar:', error);
      alert('Ocorreu um erro ao repostar. Por favor, tente novamente.');
    }
  };

  const handleCommentSubmit = async (text) => {
    try {
      await api.post(`/memes/${meme._id}/comments`, { text });
      const updatedMeme = await api.get(`/memes/${meme._id}`);
      setCommentCount(updatedMeme.data.commentCount || 0);
      setShowComments(true);
    } catch (error) {
      console.error('Erro ao comentar:', error);
      throw error;
    }
  };

  const buildUrl = (url) => {
    if (!url) return '';
    if (url.startsWith('http')) return url;
    return `${process.env.REACT_APP_API_URL || 'http://localhost:5000'}${url}`;
  };

  return (
    <Card>
      <Header>
        <Avatar 
          src={
            meme.author?.profile?.avatar
              ? buildUrl(meme.author.profile.avatar)
              : 'https://i.pravatar.cc/150?img=3'
          }
          alt={meme.author?.username}
          onClick={() => meme.author && navigate(`/users/${meme.author._id}`)}
          style={{ cursor: 'pointer' }}
          crossOrigin="anonymous"
          onError={(e) => {
            e.target.src = 'https://i.pravatar.cc/150?img=3';
            e.target.onerror = null;
          }}
        />
        <Username 
          onClick={() => meme.author && navigate(`/users/${meme.author._id}`)}
          style={{ cursor: 'pointer' }}
        >
          @{meme.author?.username || 'usuário desconhecido'}
        </Username>
      </Header>
      
      <MediaContainer>
        {meme.mediaType === 'image' ? (
          <img 
            src={buildUrl(meme.mediaUrl)}
            alt={meme.caption || 'Meme image'}
            crossOrigin="anonymous"
            onError={(e) => {
              e.target.src = 'https://placehold.co/600x400?text=Imagem+não+carregada';
              e.target.onerror = null;
            }}
          />
        ) : (
          <video 
            src={buildUrl(meme.mediaUrl)}
            controls 
            crossOrigin="anonymous"
          />
        )}
      </MediaContainer>

      {meme.caption && <Caption>{meme.caption}</Caption>}
      
      {meme.tags?.length > 0 && (
        <Tags>
          {meme.tags.map(tag => (
            <Tag key={tag}>#{tag}</Tag>
          ))}
        </Tags>
      )}

      <Actions>
        <EmojiButton
          onClick={() => handleReaction('like')}
          $animate={animateLike}
          $active={reaction === 'like'}
        >
          <span>{reaction === 'like' ? '😍' : '😊'}</span> {likeCount}
        </EmojiButton>
        
        <EmojiButton
          onClick={() => handleReaction('dislike')}
          $animate={animateDislike}
          $active={reaction === 'dislike'}
        >
          <span>{reaction === 'dislike' ? '😠' : '🙁'}</span> {dislikeCount}
        </EmojiButton>
        
        <ActionButton 
          onClick={() => setShowComments(!showComments)}
          $active={showComments}
        >
          <FiMessageSquare /> {commentCount}
        </ActionButton>
        
        <ActionButton
          onClick={handleRepost}
          $active={isReposted}
        >
          <FiRepeat /> {repostCount}
        </ActionButton>
        
        <ActionButton>
          <FiShare2 /> Compartilhar
        </ActionButton>
      </Actions>

      {showComments && (
        <CommentSection 
          memeId={meme._id}
          onCommentSubmit={handleCommentSubmit}
        />
      )}
    </Card>
  );
};

MemeCard.propTypes = {
  meme: PropTypes.shape({
    _id: PropTypes.string.isRequired,
    author: PropTypes.shape({
      _id: PropTypes.string,
      username: PropTypes.string,
      profile: PropTypes.shape({
        avatar: PropTypes.string
      })
    }),
    likes: PropTypes.array,
    dislikes: PropTypes.array,
    commentCount: PropTypes.number,
    repostCount: PropTypes.number,
    mediaType: PropTypes.oneOf(['image', 'video']),
    mediaUrl: PropTypes.string,
    caption: PropTypes.string,
    tags: PropTypes.arrayOf(PropTypes.string)
  })
};

export default MemeCard;
