import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { FiUser } from 'react-icons/fi';

const CardContainer = styled.div`
  display: flex;
  align-items: center;
  padding: 15px;
  border-bottom: 1px solid #eee;
  position: relative; /* Adicionado para posicionar o badge */
`;

const Avatar = styled.img`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  object-fit: cover;
  margin-right: 15px;
`;

const UserInfo = styled.div`
  flex: 1;
`;

const Username = styled(Link)`
  font-weight: bold;
  text-decoration: none;
  color: inherit;
  display: block;
  margin-bottom: 5px;

  &:hover {
    text-decoration: underline;
  }
`;

const Bio = styled.p`
  font-size: 14px;
  color: #666;
  margin: 0;
`;

const FollowButton = styled.button`
  padding: 5px 15px;
  border-radius: 20px;
  border: 1px solid #ddd;
  background: ${props => props.isFollowing ? '#fff' : '#000'};
  color: ${props => props.isFollowing ? '#000' : '#fff'};
  cursor: pointer;
  font-weight: bold;

  &:hover {
    opacity: 0.9;
  }
`;

// Novo componente para o badge de dias
const DaysBadge = styled.div`
  position: absolute;
  top: 10px;
  right: 10px;
  background: #f1c40f;
  color: white;
  padding: 3px 8px;
  border-radius: 10px;
  font-size: 10px;
  font-weight: bold;
  display: flex;
  align-items: center;
  gap: 3px;

  &::before {
    content: 'ðŸ“…';
    font-size: 10px;
  }
`;

const UserCard = ({ user, showFollowButton = false }) => {
  // FunÃ§Ã£o para calcular dias desde o registro
  const calculateDaysSinceJoin = () => {
    if (!user?.createdAt) return 0;
    
    try {
      const joinDate = new Date(user.createdAt);
      const today = new Date();
      const diffTime = today - joinDate;
      return Math.floor(diffTime / (1000 * 60 * 60 * 24));
    } catch (error) {
      console.error('Error calculating days:', error);
      return 0;
    }
  };

  const daysSinceJoin = calculateDaysSinceJoin();

  return (
    <CardContainer>
      {/* Badge de dias - sÃ³ mostra se daysSinceJoin > 0 */}
      {daysSinceJoin > 0 && (
        <DaysBadge title={`Membro hÃ¡ ${daysSinceJoin} dias`}>
          {daysSinceJoin}d
        </DaysBadge>
      )}

      <Avatar 
        src={user.profile?.avatar || '/default-avatar.jpg'} 
        alt={user.username}
        onError={(e) => {
          e.target.src = '/default-avatar.jpg';
        }}
      />
      <UserInfo>
        <Username to={`/profile/${user._id}`}>@{user.username}</Username>
        <Bio>{user.profile?.bio || 'No bio yet'}</Bio>
      </UserInfo>
      {showFollowButton && (
        <FollowButton>
          {false ? 'Following' : 'Follow'}
        </FollowButton>
      )}
    </CardContainer>
  );
};

export default UserCard;
