import React, { useContext } from 'react';
import { AuthContext } from '../contexts/AuthContext';
import FeedPage from './FeedPage'; // Importe o FeedPage

const HomePage = () => {
  const { user } = useContext(AuthContext);

  if (user) {
    // Se logado, mostra o FeedPage (que já tem os memes/vídeos)
    return <FeedPage />;
  }

  // Se não logado, mostra a página inicial normal
  return (
    <div>
      <h1>Bem-vindo ao iFunny Clone</h1>
      <p>Faça login para ver o conteúdo</p>
    </div>
  );
};

export default HomePage;
