import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import styled from 'styled-components';
import api from '../services/api';
import ProfileHeader from '../components/profile/ProfileHeader';
import ProfileTabs from '../components/profile/ProfileTabs';
import UserMemes from '../components/profile/UserMemes';
import UserLikes from '../components/profile/UserLikes';
import UserActivity from '../components/profile/UserActivity';
import UserGuests from '../components/profile/UserGuests';
import ProfileSettings from '../components/profile/ProfileSettings';

const ProfileContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
`;

const ProfilePage = () => {
  const { userId } = useParams();
  const [user, setUser] = useState(null);
  const [stats, setStats] = useState({
    posts: 0,
    followers: 0,
    following: 0
  });
  const [activeTab, setActiveTab] = useState('memes');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        setLoading(true);
        setError(null);
        
        // Busca dados do usuário
        const userResponse = await api.get(`/users/${userId}`);
        setUser(userResponse.data);
        
        // Busca estatísticas
        const statsResponse = await api.get(`/users/${userId}/stats`);
        setStats({
          posts: statsResponse.data.posts || 0,
          followers: statsResponse.data.followers || 0,
          following: statsResponse.data.following || 0
        });
        
      } catch (error) {
        console.error('Error fetching user data:', error);
        setError(error);
      } finally {
        setLoading(false);
      }
    };

    fetchUserData();
  }, [userId]);

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">Error loading profile</div>;
  if (!user && !loading) return <div>User not found</div>;

  return (
    <ProfileContainer>
      <ProfileHeader 
        user={user} 
        stats={stats}
      />
      <ProfileTabs activeTab={activeTab} setActiveTab={setActiveTab} />
      
      {activeTab === 'memes' && <UserMemes userId={userId} />}
      {activeTab === 'likes' && <UserLikes userId={userId} />}
      {activeTab === 'activity' && <UserActivity userId={userId} />}
      {activeTab === 'guests' && <UserGuests userId={userId} />}
      {activeTab === 'settings' && <ProfileSettings user={user} />}
    </ProfileContainer>
  );
};

export default ProfilePage;
