import React from 'react';

const MemeCard = ({ meme }) => {
  return (
    <div className="meme-card">
      <div className="meme-header">
        <img 
          src={meme.author.profile.avatar || 'https://i.pravatar.cc/150?img=3'} 
          alt={meme.author.username}
        />
        <span>@{meme.author.username}</span>
      </div>
      
      <div className="meme-content">
        <img src={meme.imageUrl} alt={meme.caption} />
        {meme.caption && <p>{meme.caption}</p>}
      </div>

      <div className="meme-actions">
        <button>üëç {meme.upvotes || 0}</button>
        <button>üí¨ {meme.commentCount || 0}</button>
        <button>‚ÜóÔ∏è Compartilhar</button>
      </div>
    </div>
  );
};

export default MemeCard;
