import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import api from '../services/api';
import { useAuth } from '../contexts/AuthContext';
import { FiSend, FiCornerDownLeft } from 'react-icons/fi';

const CommentContainer = styled.div`
  padding: 16px;
  border-top: 1px solid #f0f0f0;
`;

const CommentList = styled.div`
  max-height: 300px;
  overflow-y: auto;
  margin-bottom: 16px;
`;

const CommentItem = styled.div`
  display: flex;
  margin-bottom: 12px;
`;

const CommentAvatar = styled.img`
  width: 32px;
  height: 32px;
  border-radius: 50%;
  margin-right: 12px;
  object-fit: cover;
`;

const CommentContent = styled.div`
  flex: 1;
`;

const CommentUser = styled.div`
  font-weight: 600;
  font-size: 0.9rem;
`;

const CommentText = styled.p`
  margin: 4px 0 0;
  font-size: 0.9rem;
`;

const CommentForm = styled.form`
  display: flex;
  align-items: center;
  margin-top: 16px;
`;

const CommentInput = styled.input`
  flex: 1;
  padding: 10px 12px;
  border: 1px solid #ddd;
  border-radius: 20px;
  font-size: 0.9rem;
  outline: none;

  &:focus {
    border-color: #ff4500;
  }
`;

const SubmitButton = styled.button`
  background: #ff4500;
  color: white;
  border: none;
  width: 36px;
  height: 36px;
  border-radius: 50%;
  margin-left: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
`;

const ReplyButton = styled.button`
  background: none;
  border: none;
  color: #666;
  font-size: 0.8rem;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 4px;
  margin-top: 4px;

  &:hover {
    color: #ff4500;
  }
`;

const ReplyForm = styled.form`
  display: flex;
  margin-top: 8px;
  padding-left: 12px;
  border-left: 2px solid #eee;
`;

const CommentSection = ({ memeId, onCommentSubmit }) => {
  const { user } = useAuth();
  const [comments, setComments] = useState([]);
  const [commentText, setCommentText] = useState('');
  const [loading, setLoading] = useState(true);
  const [replyingTo, setReplyingTo] = useState(null);
  const [replyText, setReplyText] = useState('');

  useEffect(() => {
    const fetchComments = async () => {
      try {
        const response = await api.get(`/memes/${memeId}/comments`);
        setComments(response.data.comments);
      } catch (error) {
        console.error('Erro ao buscar comentários:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchComments();
  }, [memeId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!commentText.trim()) return;

    try {
      await onCommentSubmit(commentText);
      setCommentText('');
      // Atualiza a lista de comentários após adicionar novo
      const response = await api.get(`/memes/${memeId}/comments`);
      setComments(response.data.comments);
    } catch (error) {
      console.error('Erro ao enviar comentário:', error);
    }
  };

  return (
    <CommentContainer>
      <CommentList>
        {loading ? (
          <div>Carregando comentários...</div>
        ) : comments.length === 0 ? (
          <div>Nenhum comentário ainda. Seja o primeiro!</div>
        ) : (
          comments.map(comment => (
            <CommentItem key={comment._id || comment.createdAt}>
              <CommentAvatar 
                src={comment.user.profile?.avatar || 'https://i.pravatar.cc/150?img=3'} 
                alt={comment.user.username}
              />
              <CommentContent>
                <CommentUser>@{comment.user.username}</CommentUser>
                <CommentText>{comment.text}</CommentText>
              </CommentContent>
            </CommentItem>
          ))
        )}
      </CommentList>

      {user && (
        <CommentForm onSubmit={handleSubmit}>
          <CommentInput
            type="text"
            value={commentText}
            onChange={(e) => setCommentText(e.target.value)}
            placeholder="Adicione um comentário..."
          />
          <SubmitButton type="submit">
            <FiSend size={18} />
          </SubmitButton>
        </CommentForm>
      )}
    </CommentContainer>
  );
};

export default CommentSection;
