// src/components/profile/UserLikes.jsx
import { useState, useEffect } from 'react';
import styled from 'styled-components';
import api from '../../services/api';
import MemeThumbnail from '../MemeThumbnail';

const MemeGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
  gap: 20px;
  margin-top: 20px;
`;

const UserLikes = ({ userId }) => {
  const [memes, setMemes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchLikedMemes = async () => {
      try {
        const { data } = await api.get(`/users/${userId}/liked-memes`);
        setMemes(data);
      } catch (error) {
        console.error('Error fetching liked memes:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchLikedMemes();
  }, [userId]);

  if (loading) return <div>Loading liked memes...</div>;

  return (
    <MemeGrid>
      {memes.map(meme => (
        <MemeThumbnail key={meme._id} meme={meme} />
      ))}
    </MemeGrid>
  );
};

export default UserLikes;
