import React, { useState, useRef } from 'react';
import styled from 'styled-components';
import api from '../services/api';
import { useAuth } from '../contexts/AuthContext';

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
`;

const FileInputContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
`;

const Input = styled.input`
  padding: 0.75rem;
  font-size: 1rem;
  border-radius: 8px;
  border: 1px solid #ddd;
`;

const TextArea = styled.textarea`
  padding: 0.75rem;
  font-size: 1rem;
  border-radius: 8px;
  border: 1px solid #ddd;
  resize: vertical;
  min-height: 100px;
`;

const Button = styled.button`
  padding: 0.75rem;
  background-color: ${props => props.disabled ? '#ccc' : '#ff4500'};
  color: white;
  font-size: 1.1rem;
  border: none;
  border-radius: 8px;
  cursor: ${props => props.disabled ? 'not-allowed' : 'pointer'};
  transition: background 0.3s;
  
  &:hover {
    background-color: ${props => props.disabled ? '#ccc' : '#e03d00'};
  }
`;

const Preview = styled.div`
  display: flex;
  justify-content: center;
  margin: 1rem 0;

  img, video {
    max-width: 100%;
    max-height: 400px;
    border-radius: 8px;
    object-fit: contain;
  }
`;

const ErrorMessage = styled.p`
  color: #ff3333;
  margin: 0.5rem 0;
`;

const MemeUpload = ({ onSuccess }) => {
  const { user } = useAuth();
  const [file, setFile] = useState(null);
  const [caption, setCaption] = useState('');
  const [isUploading, setIsUploading] = useState(false);
  const [preview, setPreview] = useState(null);
  const [error, setError] = useState('');
  const fileInputRef = useRef(null);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setError('');
    
    if (!selectedFile) return;

    // Validação do tipo de arquivo
    if (!selectedFile.type.startsWith('image/') && !selectedFile.type.startsWith('video/')) {
      setError('Apenas imagens e vídeos são permitidos');
      return;
    }

    // Validação do tamanho do arquivo (10MB máximo)
    if (selectedFile.size > 10 * 1024 * 1024) {
      setError('O arquivo deve ter menos de 10MB');
      return;
    }

    setFile(selectedFile);
    setPreview(URL.createObjectURL(selectedFile));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!file) {
      setError('Selecione um arquivo para enviar');
      return;
    }

    setIsUploading(true);
    setError('');

    try {
      const formData = new FormData();
      formData.append('media', file);
      formData.append('caption', caption);

      await api.post('/memes', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      if (onSuccess) onSuccess();
    } catch (err) {
      console.error('Erro no upload:', err);
      setError(err.response?.data?.message || 'Erro ao enviar o meme');
    } finally {
      setIsUploading(false);
    }
  };

  return (
    <Form onSubmit={handleSubmit}>
      <FileInputContainer>
        <label htmlFor="media-upload">Selecione uma imagem ou vídeo:</label>
        <Input
          id="media-upload"
          type="file"
          accept="image/*,video/*"
          onChange={handleFileChange}
          ref={fileInputRef}
          disabled={isUploading}
        />
      </FileInputContainer>

      <TextArea
        placeholder="Escreva uma legenda (opcional)..."
        value={caption}
        onChange={(e) => setCaption(e.target.value)}
        disabled={isUploading}
      />

      {preview && (
        <Preview>
          {file.type.startsWith('image/') ? (
            <img src={preview} alt="Pré-visualização" />
          ) : (
            <video src={preview} controls />
          )}
        </Preview>
      )}

      {error && <ErrorMessage>{error}</ErrorMessage>}

      <Button type="submit" disabled={isUploading || !file}>
        {isUploading ? 'Enviando...' : 'Enviar Meme'}
      </Button>
    </Form>
  );
};

export default MemeUpload;
