// components/NotificationDropdown/NotificationDropdown.jsx
import React, { useState, useEffect, useRef } from 'react';
import styled, { keyframes } from 'styled-components';
import { useNotifications } from '../../contexts/NotificationContext';
import { FiBell, FiCheck } from 'react-icons/fi';
import { formatDistanceToNow } from 'date-fns';
import { ptBR } from 'date-fns/locale/pt-BR';

const fadeIn = keyframes`
  from { opacity: 0; transform: translateY(-10px); }
  to { opacity: 1; transform: translateY(0); }
`;

const Badge = styled.div`
  position: absolute;
  top: -5px;
  right: -5px;
  background: var(--danger);
  color: white;
  border-radius: 50%;
  width: 18px;
  height: 18px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 10px;
  font-weight: bold;
`;

const NotificationButton = styled.button`
  position: relative;
  background: none;
  border: none;
  color: white;
  cursor: pointer;
  padding: 0.5rem;
  border-radius: 50%;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  
  &:hover {
    background: rgba(255, 255, 255, 0.2);
    transform: scale(1.1);
  }
`;

const Dropdown = styled.div`
  position: absolute;
  right: 0;
  top: calc(100% + 10px);
  width: 350px;
  max-height: 500px;
  overflow-y: auto;
  background: var(--card-bg);
  border-radius: 12px;
  box-shadow: 0 8px 25px rgba(0, 0, 0, 0.15);
  display: ${({ $isOpen }) => ($isOpen ? 'block' : 'none')};
  z-index: 1001;
  animation: ${fadeIn} 0.2s ease-out;
  transform-origin: top right;
  border: 1px solid var(--border-light);

  @media (max-width: 768px) {
    width: 300px;
    right: -50px;
  }
`;

const Header = styled.div`
  padding: 1rem;
  border-bottom: 1px solid var(--border-light);
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-weight: 600;
`;

const MarkAllRead = styled.button`
  background: none;
  border: none;
  color: var(--primary);
  cursor: pointer;
  font-size: 0.85rem;
  
  &:hover {
    text-decoration: underline;
  }
`;

const NotificationList = styled.div`
  max-height: 400px;
  overflow-y: auto;
`;

const NotificationItem = styled.div`
  padding: 1rem;
  border-bottom: 1px solid var(--border-light);
  cursor: pointer;
  transition: background 0.2s ease;
  display: flex;
  gap: 0.75rem;
  background: ${({ $unread }) => ($unread ? 'rgba(255, 69, 0, 0.05)' : 'transparent')};
  
  &:hover {
    background: rgba(255, 69, 0, 0.1);
  }
`;

const Avatar = styled.img`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  object-fit: cover;
  border: 2px solid var(--border-light);
`;

const Content = styled.div`
  flex: 1;
`;

const Text = styled.div`
  font-size: 0.9rem;
  line-height: 1.4;
`;

const Time = styled.div`
  font-size: 0.75rem;
  color: var(--text-lighter);
  margin-top: 0.25rem;
`;

const ReadIndicator = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 20px;
  
  svg {
    color: var(--primary);
    opacity: ${({ $unread }) => ($unread ? 0 : 1)};
  }
`;

const EmptyState = styled.div`
  padding: 2rem 1rem;
  text-align: center;
  color: var(--text-lighter);
`;

const NotificationDropdown = () => {
  const {
    notifications,
    unreadCount,
    markAsRead,
    loading
  } = useNotifications();
  
  const [isOpen, setIsOpen] = useState(false);
  const dropdownRef = useRef(null);

  // Fecha o dropdown quando clica fora
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // Marca como lido quando o dropdown é aberto
  useEffect(() => {
    if (isOpen && unreadCount > 0) {
      const unreadIds = notifications
        .filter(n => !n.read)
        .map(n => n._id);
      
      if (unreadIds.length > 0) {
        markAsRead(unreadIds);
      }
    }
  }, [isOpen]);

  const handleItemClick = (notification) => {
    // Navega para o conteúdo relacionado
    if (notification.meme) {
      window.location.href = `/memes/${notification.meme._id}`;
    }
    setIsOpen(false);
  };

  const handleMarkAllRead = async () => {
    const allUnreadIds = notifications
      .filter(n => !n.read)
      .map(n => n._id);
    
    if (allUnreadIds.length > 0) {
      await markAsRead(allUnreadIds);
    }
  };

  const renderNotificationContent = (notification) => {
    switch(notification.type) {
      case 'like':
        return (
          <>
            <strong>{notification.sender?.username || 'Alguém'}</strong> curtiu seu meme
            {notification.meme?.caption && (
              <div style={{ fontStyle: 'italic', marginTop: '4px' }}>
                "{truncate(notification.meme.caption, 50)}"
              </div>
            )}
          </>
        );
      case 'like_grouped':
        return (
          <>
            Seu meme tem <strong>{notification.metadata?.count || 'muitas'} curtidas</strong>
            {notification.meme?.caption && (
              <div style={{ fontStyle: 'italic', marginTop: '4px' }}>
                "{truncate(notification.meme.caption, 50)}"
              </div>
            )}
          </>
        );
      case 'comment':
        return (
          <>
            <strong>{notification.sender?.username || 'Alguém'}</strong> comentou: 
            <div style={{ fontStyle: 'italic', marginTop: '4px' }}>
              "{truncate(notification.comment?.text, 60)}"
            </div>
          </>
        );
      case 'comment_grouped':
        return (
          <>
            Seu meme tem <strong>{notification.metadata?.count || 'muitos'} comentários</strong>
            {notification.meme?.caption && (
              <div style={{ fontStyle: 'italic', marginTop: '4px' }}>
                "{truncate(notification.meme.caption, 50)}"
              </div>
            )}
          </>
        );
      default:
        return 'Nova notificação';
    }
  };

  const truncate = (str, n) => {
    return str?.length > n ? str.substr(0, n-1) + '...' : str;
  };

  return (
    <div ref={dropdownRef} style={{ position: 'relative' }}>
      <NotificationButton onClick={() => setIsOpen(!isOpen)}>
        <FiBell size={20} />
        {unreadCount > 0 && <Badge>{unreadCount > 9 ? '9+' : unreadCount}</Badge>}
      </NotificationButton>
      
      <Dropdown $isOpen={isOpen}>
        <Header>
          <span>Notificações</span>
          {notifications.some(n => !n.read) && (
            <MarkAllRead onClick={handleMarkAllRead}>
              Marcar todas como lidas
            </MarkAllRead>
          )}
        </Header>
        
        <NotificationList>
          {loading ? (
            <EmptyState>Carregando...</EmptyState>
          ) : notifications.length === 0 ? (
            <EmptyState>Nenhuma notificação</EmptyState>
          ) : (
            notifications.map(notification => (
              <NotificationItem
                key={notification._id}
                $unread={!notification.read}
                onClick={() => handleItemClick(notification)}
              >
                <Avatar
                  src={
                    notification.sender?.profile?.avatar
                      ? notification.sender.profile.avatar.startsWith('https')
                        ? notification.sender.profile.avatar
                        : `${process.env.REACT_APP_API_URL || 'https://api.jokesteronline.org'}${notification.sender.profile.avatar}`
                      : 'https://i.pravatar.cc/150?img=3'
                  }
                  alt={notification.sender?.username}
                  crossOrigin="anonymous"
                  onError={(e) => {
                    e.target.src = 'https://i.pravatar.cc/150?img=3';
                    e.target.onerror = null;
                  }}
                />
                
                <Content>
                  <Text>
                    {renderNotificationContent(notification)}
                  </Text>
                  <Time>
                    {formatDistanceToNow(new Date(notification.createdAt), {
                      addSuffix: true,
                      locale: ptBR
                    })}
                  </Time>
                </Content>
                
                <ReadIndicator $unread={!notification.read}>
                  <FiCheck size={18} />
                </ReadIndicator>
              </NotificationItem>
            ))
          )}
        </NotificationList>
      </Dropdown>
    </div>
  );
};

export default NotificationDropdown;
