// ============ Comment Component ============
const Comment = memo(({ 
  comment, 
  depth = 0, 
  parentCommentId = null,
  currentUser,
  onEdit,
  onDelete,
  onReply,
  onReaction,
  editingId,
  editText,
  onEditChange,
  onSaveEdit,
  replyingTo,
  replyText,
  onReplyChange,
  onReplySubmit,
  showOptions,
  onToggleOptions
}) => {
  const isEditing = editingId === comment._id;
  const isReplying = replyingTo === comment._id;
  const isReply = depth > 0;
  const isDeleted = comment.isDeleted;
  const MAX_DEPTH_VISUAL = 5;
  const MAX_DEPTH = 5;

  const safeUser = comment.user || { 
    _id: 'deleted', 
    username: "[Removido]", 
    profile: { avatar: 'https://i.pravatar.cc/150?img=3' } 
  };

  const canEditDelete = !isDeleted && currentUser && 
    (safeUser._id === currentUser._id || currentUser.isAdmin);

  const formatTime = useCallback((dateString) => {
    const date = new Date(dateString);
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  }, []);

  const formatDate = useCallback((dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString([], { day: '2-digit', month: '2-digit', year: 'numeric' });
  }, []);

  return (
    <React.Fragment>
      <CommentItem $depth={depth}>
        <CommentAvatar 
          src={safeUser.profile?.avatar || 'https://i.pravatar.cc/150?img=3'} 
          alt={safeUser.username}
          $isReply={isReply}
        />
        <CommentContent $isReply={isReply}>
          <CommentHeader>
            <CommentUser>
              @{safeUser.username}
              {safeUser.isVerified && <UserBadge>Verificado</UserBadge>}
            </CommentUser>
            
            {canEditDelete && (
              <MoreOptionsButton onClick={() => onToggleOptions(comment._id)}>
                <FiMoreHorizontal />
              </MoreOptionsButton>
            )}
          </CommentHeader>
          
          {showOptions === comment._id && (
            <OptionsMenu>
              <OptionButton onClick={() => onEdit(comment)}>
                <FiEdit2 size={14} /> Editar
              </OptionButton>
              <OptionButton onClick={() => onDelete(comment._id, isReply, parentCommentId)}>
                <FiTrash2 size={14} /> Excluir
              </OptionButton>
              <OptionButton onClick={() => onToggleOptions(null)}>
                <FiX size={14} /> Cancelar
              </OptionButton>
            </OptionsMenu>
          )}
          
          {isEditing ? (
            <EditForm>
              <EditTextarea
                value={editText}
                onChange={(e) => onEditChange(e.target.value)}
                autoFocus
              />
              <EditButtons>
                <CancelButton onClick={() => onEdit(null)}>
                  Cancelar
                </CancelButton>
                <SaveButton onClick={() => onSaveEdit(comment._id, isReply, parentCommentId)}>
                  Salvar
                </SaveButton>
              </EditButtons>
            </EditForm>
          ) : (
            <>
              <CommentText $isDeleted={isDeleted}>
                {isDeleted ? <DeletedMessage>{comment.text}</DeletedMessage> : comment.text}
              </CommentText>
              <CommentTime>
                {formatDate(comment.createdAt)} às {formatTime(comment.createdAt)}
                {comment.isEdited && ' (editado)'}
              </CommentTime>
            </>
          )}
          
          {!isDeleted && (
            <ReactionButtons>
              <ReactionButton 
                onClick={() => onReaction(comment._id, 'like', isReply, parentCommentId)}
                $active={comment.userReaction === 'like'}
              >
                {comment.userReaction === 'like' ? <FaThumbsUp /> : <FiThumbsUp />}
                {comment.likes?.length || 0}
              </ReactionButton>
              <ReactionButton 
                onClick={() => onReaction(comment._id, 'dislike', isReply, parentCommentId)}
                $active={comment.userReaction === 'dislike'}
              >
                {comment.userReaction === 'dislike' ? <FaThumbsDown /> : <FiThumbsDown />}
                {comment.dislikes?.length || 0}
              </ReactionButton>
              {currentUser && depth < MAX_DEPTH && (
                <ReplyButton onClick={() => onReply(comment._id, parentCommentId)}>
                  <FiCornerDownLeft size={14} />
                  Responder
                </ReplyButton>
              )}
            </ReactionButtons>
          )}
        </CommentContent>
      </CommentItem>

      {isReplying && depth < MAX_DEPTH && (
        <ReplyForm onSubmit={(e) => {
          e.preventDefault();
          onReplySubmit(comment._id, depth === 0);
        }}>
          <CommentInput
            type="text"
            value={replyText}
            onChange={(e) => onReplyChange(e.target.value)}
            placeholder="Escreva uma resposta..."
            autoFocus
          />
          <SubmitButton type="submit">
            <FiSend size={18} />
          </SubmitButton>
        </ReplyForm>
      )}

      {comment.replies?.map(reply => (
        <Comment 
          key={reply._id}
          comment={reply}
          depth={depth + 1}
          parentCommentId={comment._id}
          currentUser={currentUser}
          onEdit={onEdit}
          onDelete={onDelete}
          onReply={onReply}
          onReaction={onReaction}
          editingId={editingId}
          editText={editText}
          onEditChange={onEditChange}
          onSaveEdit={onSaveEdit}
          replyingTo={replyingTo}
          replyText={replyText}
          onReplyChange={onReplyChange}
          onReplySubmit={onReplySubmit}
          showOptions={showOptions}
          onToggleOptions={onToggleOptions}
        />
      ))}
    </React.Fragment>
  );
});

// ============ Main Component ============
const CommentSection = ({ memeId, onCommentSubmit }) => {
  const { user: currentUser } = useAuth();
  const [comments, setComments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [commentText, setCommentText] = useState('');
  const [replyingTo, setReplyingTo] = useState(null);
  const [replyText, setReplyText] = useState('');
  const [editingId, setEditingId] = useState(null);
  const [editText, setEditText] = useState('');
  const [showOptions, setShowOptions] = useState(null);
  const [parentCommentId, setParentCommentId] = useState(null);

  // Fetch comments
  const fetchComments = useCallback(async () => {
    try {
      setLoading(true);
      const response = await api.get(`/memes/${memeId}/comments`);
      setComments(response.data?.comments || []);
    } catch (error) {
      console.error('Error fetching comments:', error);
    } finally {
      setLoading(false);
    }
  }, [memeId]);

  useEffect(() => {
    fetchComments();
  }, [fetchComments]);

  // Handlers
  const handleSubmit = useCallback(async (e) => {
    e.preventDefault();
    if (!commentText.trim()) return;

    try {
      await onCommentSubmit(commentText);
      setCommentText('');
      const response = await api.get(`/memes/${memeId}/comments`);
      setComments(response.data.comments);
    } catch (error) {
      console.error('Error submitting comment:', error);
    }
  }, [commentText, memeId, onCommentSubmit]);

  const handleReply = useCallback((commentId, parentId = null) => {
    setReplyingTo(replyingTo === commentId ? null : commentId);
    setParentCommentId(parentId);
    setReplyText('');
  }, [replyingTo]);

  const handleReplySubmit = useCallback(async (commentId, isCommentLevel = true) => {
    if (!replyText.trim()) return;

    try {
      const endpoint = isCommentLevel 
        ? `/memes/${memeId}/comments/${commentId}/replies`
        : `/memes/${memeId}/comments/${parentCommentId}/replies/${commentId}/replies`;

      const response = await api.post(endpoint, { text: replyText });
      
      const updateComments = (comments) => comments.map(c => {
        if (isCommentLevel && c._id === commentId) {
          return { ...c, replies: [...(c.replies || []), response.data.reply] };
        }

        if (!isCommentLevel && c.replies) {
          const updateReplies = (replies) => replies.map(r => {
            if (r._id === commentId) {
              return { ...r, replies: [...(r.replies || []), response.data.reply] };
            }
            return r.replies ? { ...r, replies: updateReplies(r.replies) } : r;
          });
          return { ...c, replies: updateReplies(c.replies) };
        }

        return c;
      });

      setComments(updateComments(comments));
      setReplyText('');
      setReplyingTo(null);
      setParentCommentId(null);
    } catch (error) {
      console.error('Error submitting reply:', error);
    }
  }, [memeId, parentCommentId, replyText]);

  const handleEdit = useCallback((comment) => {
    setEditingId(comment?._id || null);
    setEditText(comment?.text || '');
    setShowOptions(null);
  }, []);

  const handleSaveEdit = useCallback(async (commentId, isReply = false, parentId = null) => {
    try {
      const endpoint = isReply && parentId
        ? `/memes/${memeId}/comments/${parentId}/replies/${commentId}`
        : `/memes/${memeId}/comments/${commentId}`;

      const response = await api.put(endpoint, { text: editText });

      const updateComments = (comments) => comments.map(c => {
        if (!isReply && c._id === commentId) {
          return { ...c, ...response.data.comment };
        }

        if (isReply && c._id === parentId) {
          const updateReplies = (replies) => replies.map(r => {
            if (r._id === commentId) return { ...r, ...response.data.reply };
            return r.replies ? { ...r, replies: updateReplies(r.replies) } : r;
          });
          return { ...c, replies: updateReplies(c.replies || []) };
        }

        return c;
      });

      setComments(updateComments(comments));
      setEditingId(null);
      setEditText('');
    } catch (error) {
      console.error('Error editing comment:', error);
    }
  }, [editText, memeId]);

  const handleDelete = useCallback(async (commentId, isReply = false, parentId = null) => {
    if (!window.confirm('Tem certeza que deseja excluir?')) return;
    
    try {
      const endpoint = isReply && parentId
        ? `/memes/${memeId}/comments/${parentId}/replies/${commentId}`
        : `/memes/${memeId}/comments/${commentId}`;

      await api.delete(endpoint);

      const updateComments = (comments) => comments.map(c => {
        if (!isReply && c._id === commentId) {
          return { 
            ...c, 
            isDeleted: true,
            text: "[Comentário removido]",
            user: { 
              _id: 'deleted',
              username: "[Removido]",
              profile: { avatar: 'https://i.pravatar.cc/150?img=3' }
            }
          };
        }

        if (isReply && c._id === parentId) {
          const updateReplies = (replies) => replies.map(r => {
            if (r._id === commentId) {
              return { 
                ...r, 
                isDeleted: true,
                text: "[Resposta removida]",
                user: { 
                  _id: 'deleted',
                  username: "[Removido]",
                  profile: { avatar: 'https://i.pravatar.cc/150?img=3' }
                }
              };
            }
            return r.replies ? { ...r, replies: updateReplies(r.replies) } : r;
          });
          return { ...c, replies: updateReplies(c.replies || []) };
        }

        return c;
      });

      setComments(updateComments(comments));
      setShowOptions(null);
    } catch (error) {
      console.error('Error deleting comment:', error);
    }
  }, [memeId]);

  const handleReaction = useCallback(async (commentId, reaction, isReply = false, parentId = null) => {
    try {
      const endpoint = isReply && parentId
        ? `/memes/${memeId}/comments/${parentId}/replies/${commentId}/reaction`
        : `/memes/${memeId}/comments/${commentId}/reaction`;

      const response = await api.post(endpoint, { reaction });

      const updateComments = (comments) => comments.map(c => {
        if (!isReply && c._id === commentId) {
          return { 
            ...c, 
            likes: response.data.likes,
            dislikes: response.data.dislikes,
            userReaction: response.data.userReaction
          };
        }

        if (isReply && c._id === parentId) {
          const updateReplies = (replies) => replies.map(r => {
            if (r._id === commentId) {
              return { 
                ...r, 
                likes: response.data.likes,
                dislikes: response.data.dislikes,
                userReaction: response.data.userReaction
              };
            }
            return r.replies ? { ...r, replies: updateReplies(r.replies) } : r;
          });
          return { ...c, replies: updateReplies(c.replies || []) };
        }

        return c;
      });

      setComments(updateComments(comments));
    } catch (error) {
      console.error('Error reacting to comment:', error);
    }
  }, [memeId]);

  return (
    <CommentContainer>
      <CommentList>
        {loading ? (
          <LoadingMessage>Carregando comentários...</LoadingMessage>
        ) : comments.length > 0 ? (
          comments.map(comment => (
            <Comment 
              key={comment._id}
              comment={comment}
              depth={0}
              currentUser={currentUser}
              onEdit={handleEdit}
              onDelete={handleDelete}
              onReply={handleReply}
              onReaction={handleReaction}
              editingId={editingId}
              editText={editText}
              onEditChange={setEditText}
              onSaveEdit={handleSaveEdit}
              replyingTo={replyingTo}
              replyText={replyText}
              onReplyChange={setReplyText}
              onReplySubmit={handleReplySubmit}
              showOptions={showOptions}
              onToggleOptions={setShowOptions}
            />
          ))
        ) : (
          <EmptyMessage>Nenhum comentário ainda. Seja o primeiro!</EmptyMessage>
        )}
      </CommentList>

      {currentUser && (
        <CommentForm onSubmit={handleSubmit}>
          <CommentInput
            type="text"
            value={commentText}
            onChange={(e) => setCommentText(e.target.value)}
            placeholder="Adicione um comentário..."
          />
          <SubmitButton type="submit">
            <FiSend size={18} />
          </SubmitButton>
        </CommentForm>
      )}
    </CommentContainer>
  );
};

export default CommentSection;
