// src/components/MemeThumbnail.jsx
import styled from 'styled-components';
import { Link } from 'react-router-dom';

const ThumbnailContainer = styled(Link)`
  display: block;
  border-radius: 8px;
  overflow: hidden;
  position: relative;
  aspect-ratio: 1;
  background: #f5f5f5;
`;

const ThumbnailImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.3s;

  &:hover {
    transform: scale(1.05);
  }
`;

const MemeThumbnail = ({ meme }) => {
    // Construa a URL correta
    const getMediaUrl = () => {
      if (!meme.mediaUrl) return ''; // Ou uma imagem padrão
      if (meme.mediaUrl.startsWith('http')) return meme.mediaUrl;
      return `${process.env.REACT_APP_API_URL || 'http://localhost:5000'}${meme.mediaUrl}`;
    };
  
    return (
      <ThumbnailContainer to={`/memes/${meme._id}`}>
        <ThumbnailImage 
          src={getMediaUrl()}
          alt={meme.caption || 'Meme thumbnail'}
          crossOrigin="anonymous"
          onError={(e) => {
            e.target.src = 'https://placehold.co/600x400?text=Imagem+não+carregada';
            e.target.onerror = null;
          }}
        />
      </ThumbnailContainer>
    );
  };

export default MemeThumbnail;
