// pages/MemeDetailPage.jsx
import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { useAuth } from '../contexts/AuthContext';
import { useNotifications } from '../contexts/NotificationContext';
import api from '../services/api';
import { FiArrowLeft, FiHeart, FiMessageSquare, FiShare2, FiMoreVertical } from 'react-icons/fi';
import CommentSection from '../components/CommentSection';
import { formatDistanceToNow } from 'date-fns';
import { ptBR } from 'date-fns/locale';

// Estilos
const MemeDetailContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 20px;
  background: var(--background);
  color: var(--text);
`;

const Header = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 20px;
  padding-bottom: 10px;
  border-bottom: 1px solid var(--border-light);
`;

const BackButton = styled.button`
  background: none;
  border: none;
  color: var(--primary);
  cursor: pointer;
  margin-right: 15px;
  font-size: 1.2rem;
  display: flex;
  align-items: center;
`;

const MemeContent = styled.div`
  margin-bottom: 30px;
`;

const MemeMedia = styled.div`
  width: 100%;
  max-height: 600px;
  margin-bottom: 15px;
  border-radius: var(--radius-md);
  overflow: hidden;
  background: var(--background-light);
  display: flex;
  justify-content: center;
  align-items: center;

  img, video {
    width: 100%;
    max-height: 600px;
    object-fit: contain;
    display: block;
  }

  video {
    background: #000;
  }
`;

const Caption = styled.p`
  font-size: 1rem;
  margin-bottom: 15px;
  line-height: 1.4;
  white-space: pre-wrap;
  word-break: break-word;
`;

const UserInfo = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 15px;
`;

const Avatar = styled.img`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  object-fit: cover;
  margin-right: 10px;
  border: 2px solid var(--border-light);
  cursor: pointer;
`;

const Username = styled.span`
  font-weight: 600;
  color: var(--primary);
  cursor: pointer;
`;

const Timestamp = styled.span`
  font-size: 0.8rem;
  color: var(--text-lighter);
  margin-left: auto;
`;

const Actions = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 10px 0;
  border-top: 1px solid var(--border-light);
  border-bottom: 1px solid var(--border-light);
  margin-bottom: 20px;
`;

const ActionButton = styled.button`
  background: none;
  border: none;
  color: var(--text-light);
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 5px;
  font-size: 0.9rem;
  padding: 8px 12px;
  border-radius: var(--radius-sm);
  transition: var(--transition);

  &:hover {
    background: rgba(255, 69, 0, 0.1);
    color: var(--primary);
  }

  &.active {
    color: var(--primary);
  }
`;

const Tags = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
  margin-bottom: 20px;
`;

const Tag = styled.span`
  background: rgba(255, 69, 0, 0.1);
  color: var(--primary);
  padding: 4px 10px;
  border-radius: 20px;
  font-size: 0.8rem;
`;

const Loading = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 200px;
  color: var(--text-light);
`;

const ErrorMessage = styled.div`
  background: rgba(255, 0, 0, 0.1);
  color: var(--danger);
  padding: 15px;
  border-radius: var(--radius-sm);
  text-align: center;
  margin: 20px 0;
`;

const MemeDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const { markAsRead } = useNotifications();
  const [meme, setMeme] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [liked, setLiked] = useState(false);
  const [likeCount, setLikeCount] = useState(0);
  const [commentCount, setCommentCount] = useState(0);

  // Busca os detalhes do meme
  const fetchMeme = useCallback(async () => {
    try {
      setLoading(true);
      const response = await api.get(`/memes/${id}`);
      const memeData = response.data;
      
      setMeme(memeData);
      setLiked(memeData.likes.includes(user?._id));
      setLikeCount(memeData.likes.length);
      setCommentCount(memeData.comments?.length || 0);
      
      // Marca notificações relacionadas como lidas
      if (user) {
        markAsRead([id]); // Assumindo que o ID da notificação é o mesmo do meme
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Erro ao carregar o meme');
    } finally {
      setLoading(false);
    }
  }, [id, user?._id, markAsRead]);

  // Atualiza o contador de comentários
  const updateCommentCount = useCallback((count) => {
    setCommentCount(prev => prev + count);
  }, []);

  // Manipula o like/unlike
  const handleLike = useCallback(async () => {
    if (!user) {
      navigate('/login');
      return;
    }

    try {
      if (liked) {
        await api.delete(`/memes/${id}/like`);
        setLikeCount(prev => prev - 1);
      } else {
        await api.post(`/memes/${id}/like`);
        setLikeCount(prev => prev + 1);
      }
      setLiked(!liked);
    } catch (err) {
      console.error('Erro ao curtir:', err);
    }
  }, [id, liked, user, navigate]);

  // Compartilha o link do meme
  const handleShare = useCallback(() => {
    navigator.clipboard.writeText(`${window.location.origin}/memes/${id}`);
    alert('Link copiado para a área de transferência!');
  }, [id]);

  // Atualiza o contador de comentários quando um novo é adicionado
  const handleCommentSubmit = useCallback(async () => {
    updateCommentCount(1);
    await fetchMeme(); // Recarrega o meme para garantir que os dados estejam atualizados
  }, [fetchMeme, updateCommentCount]);

  useEffect(() => {
    fetchMeme();
  }, [fetchMeme]);

  if (loading) {
    return (
      <MemeDetailContainer>
        <Loading>Carregando meme...</Loading>
      </MemeDetailContainer>
    );
  }

  if (error) {
    return (
      <MemeDetailContainer>
        <ErrorMessage>{error}</ErrorMessage>
      </MemeDetailContainer>
    );
  }

  if (!meme) {
    return (
      <MemeDetailContainer>
        <ErrorMessage>Meme não encontrado</ErrorMessage>
      </MemeDetailContainer>
    );
  }

  const isVideo = meme.mediaUrl?.endsWith('.mp4') || meme.mediaType === 'video';

  return (
    <MemeDetailContainer>
      <Header>
        <BackButton onClick={() => navigate(-1)}>
          <FiArrowLeft />
        </BackButton>
        <h2>Detalhes do Meme</h2>
      </Header>

      <MemeContent>
        <UserInfo>
          <Avatar 
            src={
              meme.author.profile?.avatar 
                ? meme.author.profile.avatar.startsWith('http')
                  ? meme.author.profile.avatar
                  : `${process.env.REACT_APP_API_URL || 'https://api.jokesteronline.org'}${meme.author.profile.avatar}`
                : 'https://i.pravatar.cc/150?img=3'
            }
            alt={meme.author.username}
            onClick={() => navigate(`/users/${meme.author._id}`)}
            onError={(e) => {
              e.target.src = 'https://i.pravatar.cc/150?img=3';
            }}
          />
          <Username onClick={() => navigate(`/users/${meme.author._id}`)}>
            @{meme.author.username}
          </Username>
          <Timestamp>
            {formatDistanceToNow(new Date(meme.createdAt), {
              addSuffix: true,
              locale: ptBR
            })}
          </Timestamp>
        </UserInfo>

        {meme.caption && <Caption>{meme.caption}</Caption>}

        {meme.tags && meme.tags.length > 0 && (
          <Tags>
            {meme.tags.map((tag, index) => (
              <Tag key={index}>#{tag}</Tag>
            ))}
          </Tags>
        )}

        <MemeMedia>
          {isVideo ? (
            <video controls>
              <source 
                src={
                  meme.mediaUrl.startsWith('http')
                    ? meme.mediaUrl
                    : `${process.env.REACT_APP_API_URL || 'https://api.jokesteronline.org'}${meme.mediaUrl}`
                } 
                type="video/mp4" 
              />
              Seu navegador não suporta vídeos HTML5.
            </video>
          ) : (
            <img
              src={
                meme.mediaUrl.startsWith('http')
                  ? meme.mediaUrl
                  : `${process.env.REACT_APP_API_URL || 'https://api.jokesteronline.org'}${meme.mediaUrl}`
              }
              alt={meme.caption || 'Meme'}
              onError={(e) => {
                e.target.src = 'https://via.placeholder.com/600x400?text=Imagem+nao+carregada';
              }}
            />
          )}
        </MemeMedia>

        <Actions>
          <ActionButton 
            onClick={handleLike}
            className={liked ? 'active' : ''}
          >
            <FiHeart fill={liked ? 'var(--primary)' : 'none'} />
            {likeCount}
          </ActionButton>
          <ActionButton>
            <FiMessageSquare />
            {commentCount}
          </ActionButton>
          <ActionButton onClick={handleShare}>
            <FiShare2 />
            Compartilhar
          </ActionButton>
          {user?._id === meme.author._id && (
            <ActionButton onClick={() => navigate(`/memes/${id}/edit`)}>
              <FiMoreVertical />
              Editar
            </ActionButton>
          )}
        </Actions>
      </MemeContent>

      <CommentSection 
        memeId={id} 
        currentUser={user}
        onCommentSubmit={handleCommentSubmit}
      />
    </MemeDetailContainer>
  );
};

export default MemeDetailPage;
