import React, { useState, useEffect, useRef } from 'react';
import styled, { keyframes } from 'styled-components';
import { FiMessageSquare, FiShare2, FiRepeat } from 'react-icons/fi';
import { FaSmile, FaAngry, FaCheck } from 'react-icons/fa';
import api from '../services/api';
import { useAuth } from '../contexts/AuthContext';
import CommentSection from './CommentSection';
import { useNavigate } from 'react-router-dom';

// Animations
const popIn = keyframes`
  0% { transform: scale(0.5); opacity: 0; }
  70% { transform: scale(1.2); opacity: 0.7; }
  100% { transform: scale(1); opacity: 1; }
`;

const pulse = keyframes`
  0% { transform: scale(1); }
  50% { transform: scale(1.1); }
  100% { transform: scale(1); }
`;

const floating = keyframes`
  0% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
  100% { transform: translateY(0px); }
`;

const gradientBackground = keyframes`
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
`;

const fadeIn = keyframes`
  from { opacity: 0; }
  to { opacity: 1; }
`;

// Estilos
const Card = styled.div`
  background: var(--card-bg);
  border-radius: var(--radius-md);
  margin-bottom: 1.5rem;
  box-shadow: var(--shadow);
  overflow: hidden;
  transition: var(--transition);
  position: relative;
  border: 1px solid var(--border);
  
  &:hover {
    box-shadow: var(--shadow-hover);
  }
`;

const RepostBadge = styled.div`
  position: absolute;
  top: 0.625rem;
  left: 0.625rem;
  background: linear-gradient(45deg, var(--primary), var(--primary-hover));
  color: var(--card-bg);
  padding: 0.375rem 0.75rem;
  border-radius: 1.25rem;
  font-size: 0.8rem;
  display: flex;
  align-items: center;
  z-index: 1;
  box-shadow: 0 2px 8px rgba(var(--primary-rgb), 0.3);
  text-shadow: 0 1px 2px rgba(0,0,0,0.2);
  animation: ${floating} 3s ease-in-out infinite;
  
  svg {
    margin-right: 0.25rem;
  }
`;

const Header = styled.div`
  display: flex;
  align-items: center;
  padding: 1rem;
  background: var(--background);
  position: relative;
  border-bottom: 1px solid var(--border-light);
`;

const Avatar = styled.img`
  width: 3.125rem;
  height: 3.125rem;
  border-radius: 50%;
  margin-right: 0.75rem;
  object-fit: cover;
  border: 3px solid var(--primary);
  transition: var(--transition);
  position: relative;
  overflow: hidden;
  cursor: pointer;
  
  &::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: linear-gradient(45deg, transparent, rgba(255,255,255,0.8), transparent);
    transform: rotate(45deg);
    animation: ${gradientBackground} 3s infinite linear;
  }
  
  &:hover {
    transform: rotate(15deg) scale(1.1);
    border-color: var(--primary-hover);
  }
`;

const Username = styled.span`
  font-weight: 700;
  font-size: 1rem;
  color: var(--text);
  transition: var(--transition);
  cursor: pointer;
  position: relative;
  
  &::after {
    content: '';
    position: absolute;
    bottom: -2px;
    left: 0;
    width: 0;
    height: 2px;
    background: var(--primary);
    transition: width 0.3s;
  }
  
  &:hover {
    color: var(--primary);
    
    &::after {
      width: 100%;
    }
  }
`;

const ResponsiveImage = styled.img`
  max-width: 100%;
  max-height: 100%;
  width: auto;
  height: auto;
  object-fit: contain;
  transition: var(--transition);
  border-radius: var(--radius-md);
  
  &:hover {
    transform: scale(1.03);
    filter: saturate(1.2) brightness(1.05);
  }
`;

const ResponsiveVideo = styled.video`
  max-width: 100%;
  max-height: 100%;
  width: auto;
  height: auto;
  object-fit: contain;
  border-radius: var(--radius-md);
  z-index: 1;
  transition: var(--transition);
  
  &:hover {
    transform: scale(1.03);
    filter: saturate(1.2) brightness(1.05);
  }
`;

const MediaContainer = styled.div`
  width: 100%;
  max-height: 43.75rem;
  min-height: 18.75rem;
  background: var(--background);
  color: var(--text);
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
  overflow: hidden;
`;

const VideoContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  overflow: hidden;
  border-radius: var(--radius-md);
`;

const PlayButton = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 5rem;
  height: 5rem;
  background: rgba(var(--primary-rgb), 0.8);
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 2;
  transition: var(--transition);
  backdrop-filter: blur(2px);
  box-shadow: var(--shadow);
  
  &::before {
    content: '';
    position: absolute;
    width: 0;
    height: 0;
    border-top: 0.9375rem solid transparent;
    border-bottom: 0.9375rem solid transparent;
    border-left: 1.5625rem solid var(--card-bg);
    margin-left: 0.3125rem;
  }
  
  &:hover {
    background: rgba(var(--primary-rgb), 0.9);
    transform: translate(-50%, -50%) scale(1.1);
  }
`;

const VideoControls = styled.div`
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  background: linear-gradient(to top, rgba(0,0,0,0.7), transparent);
  padding: 0.625rem;
  display: flex;
  align-items: center;
  z-index: 3;
  opacity: 0;
  transition: var(--transition);
  
  &:hover {
    opacity: 1;
    animation: ${fadeIn} 0.3s forwards;
  }
`;

const ControlButton = styled.button`
  background: none;
  border: none;
  color: var(--card-bg);
  font-size: 1.2rem;
  margin: 0 0.3125rem;
  cursor: pointer;
  transition: var(--transition);
  
  &:hover {
    color: var(--primary);
    transform: scale(1.2);
  }
`;

const ProgressBar = styled.div`
  flex-grow: 1;
  height: 0.25rem;
  background: rgba(255,255,255,0.3);
  border-radius: 0.125rem;
  margin: 0 0.625rem;
  position: relative;
  overflow: hidden;
  cursor: pointer;
`;

const Progress = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  width: ${props => props.$progress || '0%'};
  background: var(--primary);
  border-radius: 0.125rem;
`;

const TimeDisplay = styled.span`
  color: var(--card-bg);
  font-size: 0.8rem;
  min-width: 5rem;
  text-align: center;
`;

const Caption = styled.p`
  padding: 1rem;
  margin: 0;
  font-size: 0.95rem;
  line-height: 1.5;
  color: var(--text);
  background: var(--card-bg);
  position: relative;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 0.25rem;
    height: 100%;
    background: linear-gradient(to bottom, var(--primary), var(--primary-hover));
  }
`;

const Tags = styled.div`
  padding: 0 1rem 0.75rem;
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  background: var(--card-bg);
`;

const Tag = styled.span`
  color: var(--card-bg);
  font-size: 0.85rem;
  background: linear-gradient(45deg, var(--primary), var(--primary-hover));
  padding: 0.375rem 0.75rem;
  border-radius: 1.25rem;
  transition: var(--transition);
  text-shadow: 0 1px 2px rgba(0,0,0,0.2);
  cursor: pointer;
  
  &:hover {
    transform: translateY(-3px) scale(1.05);
    box-shadow: 0 4px 12px rgba(var(--primary-rgb), 0.3);
  }
`;

const Actions = styled.div`
  display: flex;
  padding: 0.75rem 1rem;
  border-top: 1px solid var(--border-light);
  background: var(--card-bg);
  justify-content: space-between;
`;

const ActionGroup = styled.div`
  display: flex;
  gap: 0.5rem;
`;

const ActionButton = styled.button`
  background: none;
  border: none;
  display: flex;
  align-items: center;
  color: ${props => props.$active ? props.$color || 'var(--primary)' : 'var(--text-light)'};
  cursor: pointer;
  font-size: 0.9rem;
  padding: 0.5rem 1rem;
  border-radius: var(--radius-sm);
  transition: var(--transition);
  position: relative;
  overflow: hidden;
  
  svg {
    margin-right: 0.375rem;
    font-size: 1.2rem;
    transition: var(--transition);
  }

  &:hover {
    background: rgba(var(--primary-rgb), 0.05);
    color: ${props => props.$color || 'var(--primary)'};
    transform: translateY(-2px);
    
    svg {
      transform: scale(1.2);
    }
  }
  
  &::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 0;
    height: 0;
    background: rgba(255,255,255,0.2);
    border-radius: 50%;
    transform: translate(-50%, -50%);
    transition: var(--transition);
  }
  
  &:hover::after {
    width: 120%;
    height: 120%;
  }
  
  &:active {
    transform: scale(0.95) translateY(-2px);
  }
`;

const RepostButton = styled(ActionButton)`
  color: ${props => props.$active ? 'var(--success)' : 'var(--text-light)'};
  
  &:hover {
    color: var(--success);
  }
`;

const ReactionAnimation = styled.div`
  position: absolute;
  animation: ${popIn} 0.5s forwards, ${pulse} 0.5s 0.5s forwards;
  font-size: 2.5rem;
  opacity: 0;
  pointer-events: none;
  filter: drop-shadow(0 4px 8px rgba(0,0,0,0.2));
  text-shadow: 0 2px 4px rgba(0,0,0,0.3);
  z-index: 10;
`;

const MemeCard = ({ meme, isRepost = false }) => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [isLiked, setIsLiked] = useState(false);
  const [likeCount, setLikeCount] = useState(meme.likes?.length || 0);
  const [showComments, setShowComments] = useState(false);
  const [commentCount, setCommentCount] = useState(meme.commentCount || 0);
  const [reaction, setReaction] = useState(null);
  const [isReposted, setIsReposted] = useState(false);
  const [repostCount, setRepostCount] = useState(meme.repostCount || 0);
  const [isCheckingRepost, setIsCheckingRepost] = useState(true);

  // Estados para o player de v√≠deo customizado
  const [isPlaying, setIsPlaying] = useState(false);
  const [progress, setProgress] = useState(0);
  const [duration, setDuration] = useState(0);
  const [currentTime, setCurrentTime] = useState(0);
  const videoRef = useRef(null);

  useEffect(() => {
    if (user && meme.likes) {
      setIsLiked(meme.likes.some(like => like.toString() === user._id));
    }
    setLikeCount(meme.likes?.length || 0);
    setCommentCount(meme.commentCount || 0);
    setRepostCount(meme.repostCount || 0);
    
    if (user) {
      checkIfReposted();
    } else {
      setIsCheckingRepost(false);
    }
  }, [meme, user]);

  const togglePlay = () => {
    if (videoRef.current) {
      if (isPlaying) {
        videoRef.current.pause();
      } else {
        videoRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const handleTimeUpdate = () => {
    if (videoRef.current) {
      const currentProgress = (videoRef.current.currentTime / videoRef.current.duration) * 100;
      setProgress(currentProgress);
      setCurrentTime(videoRef.current.currentTime);
    }
  };

  const handleLoadedMetadata = () => {
    if (videoRef.current) {
      setDuration(videoRef.current.duration);
    }
  };

  const handleSeek = (e) => {
    if (videoRef.current) {
      const rect = e.target.getBoundingClientRect();
      const pos = (e.clientX - rect.left) / rect.width;
      videoRef.current.currentTime = pos * videoRef.current.duration;
    }
  };

  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = Math.floor(time % 60);
    return `${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
  };

  const checkIfReposted = async () => {
    try {
      if (meme.originalMeme && meme.author._id === user._id) {
        setIsReposted(true);
      } else {
        setIsReposted(meme.reposts?.some(repost => repost.toString() === user._id));
      }
    } catch (error) {
      console.error('Erro ao verificar repost:', error);
    } finally {
      setIsCheckingRepost(false);
    }
  };

  const handleLike = async () => {
    if (!user) return;
    
    try {
      const response = await api.post(`/memes/${meme._id}/like`);
      setIsLiked(response.data.isLiked);
      setLikeCount(response.data.likeCount);
      setReaction(response.data.isLiked ? 'like' : 'dislike');
      setTimeout(() => setReaction(null), 1500);
    } catch (error) {
      console.error('Erro ao curtir:', error);
    }
  };

  const handleDislike = async () => {
    if (!user || !isLiked) return;
    
    try {
      const response = await api.post(`/memes/${meme._id}/like`);
      setIsLiked(false);
      setLikeCount(response.data.likeCount);
      setReaction('dislike');
      setTimeout(() => setReaction(null), 1500);
    } catch (error) {
      console.error('Erro ao remover curtida:', error);
    }
  };

  const handleRepost = async () => {
    if (!user) return;
    
    if (meme.author?._id === user._id) {
      setReaction('self-repost');
      setTimeout(() => setReaction(null), 1500);
      return;
    }
    
    if (isReposted || isRepost) return;

    try {
      const response = await api.post(`/memes/${meme._id}/repost`);
      setIsReposted(true);
      setRepostCount(response.data.repostCount);
      
      setReaction('repost');
      setTimeout(() => setReaction(null), 1500);
    } catch (error) {
      console.error('Erro ao repostar:', error);
      if (error.response && error.response.status === 409) {
        setIsReposted(true);
      }
    }
  };

  const handleCommentSubmit = async (formData) => {
    try {
      const response = await api.post(
        `/memes/${meme._id}/comments`,
        formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        }
      );
  
      const updatedMeme = await api.get(`/memes/${meme._id}`);
      setCommentCount(updatedMeme.data.commentCount || 0);
      
      return response.data;
    } catch (error) {
      console.error('Erro detalhado:', {
        message: error.message,
        response: error.response?.data,
        status: error.response?.status,
        config: error.config
      });
  
      throw new Error(error.response?.data?.message || error.message || 'Erro ao enviar coment√°rio');
    }
  };

  const buildUrl = (url) => {
    if (!url) {
      console.warn('URL is empty or undefined');
      return 'https://i.pravatar.cc/150?img=3'; // Fallback expl√≠cito
    }
  
    // Se j√° for uma URL completa, retorna diretamente
    if (url.startsWith('http')) {
      return url;
    }
  
    // Adiciona o prefixo da API apenas se necess√°rio
    const apiUrl = process.env.REACT_APP_API_URL || 'https://100.93.57.95:5000';
    const fullUrl = `${apiUrl}${url}`;
    
    return fullUrl;
  };

  return (
    <Card>
      {isRepost && (
        <RepostBadge>
          <FiRepeat /> Repostado por @{meme.repostedBy?.username || 'usu√°rio'}
        </RepostBadge>
      )}
      
      {reaction && (
        <ReactionAnimation style={{
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          color: reaction === 'like' ? 'var(--primary)' : 
                reaction === 'dislike' ? '#f00' : 
                reaction === 'repost' ? '#10b981' : 'var(--primary)'
        }}>
          {reaction === 'like' ? <FaSmile /> : 
           reaction === 'dislike' ? <FaAngry /> : 
           reaction === 'repost' ? <FaCheck /> : 
           <span style={{ fontSize: '1rem' }}>Voc√™ √© o autor!</span>}
        </ReactionAnimation>
      )}
      
      <Header>
        <Avatar 
          src={
            meme.author?.profile?.avatar
              ? buildUrl(meme.author.profile.avatar)
              : 'https://i.pravatar.cc/150?img=3'
          }
          alt={meme.author?.username}
          onClick={() => meme.author && navigate(`/users/${meme.author._id}`)}
          crossOrigin="anonymous"
          onError={(e) => {
            e.target.src = 'https://i.pravatar.cc/150?img=3';
          }}
        />
        <Username 
          onClick={() => meme.author && navigate(`/users/${meme.author._id}`)}
        >
          @{meme.author?.username || 'usu√°rio desconhecido'}
        </Username>
      </Header>
      
      <MediaContainer>
        {meme.mediaType === 'image' ? (
          <ResponsiveImage 
            src={buildUrl(meme.mediaUrl)}
            alt={meme.caption || 'Meme image'}
            style={{
              transform: showComments ? 'scale(0.98)' : 'scale(1)',
              filter: showComments ? 'brightness(0.95)' : 'none'
            }}
            crossOrigin="anonymous"
            onError={(e) => {
              e.target.src = 'https://placehold.co/600x400?text=Imagem+n√£o+carregada';
              e.target.onerror = null;
            }}
          />
        ) : (
          <VideoContainer onClick={togglePlay}>
            <ResponsiveVideo
              ref={videoRef}
              src={buildUrl(meme.mediaUrl)}
              preload="metadata"
              onTimeUpdate={handleTimeUpdate}
              onLoadedMetadata={handleLoadedMetadata}
              onEnded={() => setIsPlaying(false)}
              crossOrigin="anonymous"
            />
            
            {!isPlaying && (
              <PlayButton onClick={(e) => {
                e.stopPropagation();
                togglePlay();
              }} />
            )}
            
            <VideoControls onClick={(e) => e.stopPropagation()}>
              <ControlButton onClick={togglePlay}>
                {isPlaying ? '‚ùö‚ùö' : '‚ñ∂'}
              </ControlButton>
              <ProgressBar onClick={handleSeek}>
                <Progress $progress={`${progress}%`} />
              </ProgressBar>
              <TimeDisplay>
                {formatTime(currentTime)} / {formatTime(duration)}
              </TimeDisplay>
              <ControlButton onClick={() => {
                if (videoRef.current) {
                  videoRef.current.muted = !videoRef.current.muted;
                }
              }}>
                {videoRef.current?.muted ? 'üîá' : 'üîà'}
              </ControlButton>
            </VideoControls>
            
            <div style={{
              position: 'absolute',
              top: 0,
              left: 0,
              right: 0,
              bottom: 0,
              background: `linear-gradient(45deg, 
                rgba(255,255,255,0.1), 
                rgba(255,69,0,0.05))`,
              pointerEvents: 'none'
            }}/>
          </VideoContainer>
        )}
      </MediaContainer>

      {meme.caption && <Caption>{meme.caption}</Caption>}
      
      {meme.tags?.length > 0 && (
        <Tags>
          {meme.tags.map(tag => (
            <Tag key={tag} onClick={() => navigate(`/explore?tag=${tag}`)}>#{tag}</Tag>
          ))}
        </Tags>
      )}

      <Actions>
        <ActionGroup>
          <ActionButton 
            onClick={handleLike} 
            $active={isLiked}
            $color="var(--primary)"
          >
            <FaSmile /> {likeCount}
          </ActionButton>
          <ActionButton 
            onClick={handleDislike} 
            $active={!isLiked && likeCount > 0}
            $color="var(--text)"
          >
            <FaAngry /> 
          </ActionButton>
          <ActionButton 
            onClick={() => setShowComments(!showComments)}
            $active={showComments}
            $color="#f000"
          >
            <FiMessageSquare /> {commentCount}
          </ActionButton>
        </ActionGroup>
        
        <ActionGroup>
          <RepostButton 
            onClick={handleRepost}
            $active={isReposted}
            disabled={isReposted || isCheckingRepost || isRepost || (meme.author?._id === user?._id)}
            title={meme.author?._id === user?._id ? "Voc√™ n√£o pode repostar seu pr√≥prio conte√∫do" : ""}
          >
            {isCheckingRepost ? (
              <FiRepeat />
            ) : isReposted ? (
              <FaCheck />
            ) : (
              <FiRepeat />
            )} {repostCount}
          </RepostButton>
          <ActionButton>
            <FiShare2 />
          </ActionButton>
        </ActionGroup>
      </Actions>

      {showComments && (
        <CommentSection 
          memeId={meme._id}
          onCommentSubmit={handleCommentSubmit}
        />
      )}
    </Card>
  );
};

export default MemeCard;
