import React, { useState, useEffect } from 'react'; // Adicione useEffect aqui
import { useNavigate, Link, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { login } from '../services/auth'; // Adicione esta importação
import AuthForm from '../components/AuthForm';

const LoginPage = () => {
  const [credentials, setCredentials] = useState({
    usernameOrEmail: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  
  const { user, verifyAuth } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token && !initialCheckDone) return;
    
    if (user) {
      navigate(location.state?.from?.pathname || '/feed', { replace: true });
    }
  }, [user, initialCheckDone, navigate, location]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');
    
    try {
      const response = await login(credentials);
      localStorage.setItem('token', response.token);
      await verifyAuth();
    } catch (err) {
      setError(err.message || 'Credenciais inválidas');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <AuthForm
      title="Login"
      onSubmit={handleSubmit}
      error={error}
      isLoading={isLoading}
    >
      <input
        type="text"
        placeholder="Usuário ou Email"
        value={credentials.usernameOrEmail}
        onChange={(e) => setCredentials({...credentials, usernameOrEmail: e.target.value})}
        required
      />
      <input
        type="password"
        placeholder="Senha"
        value={credentials.password}
        onChange={(e) => setCredentials({...credentials, password: e.target.value})}
        required
      />
      <button type="submit" disabled={isLoading}>
        {isLoading ? 'Entrando...' : 'Entrar'}
      </button>
      <p>
        Não tem conta? <Link to="/register">Cadastre-se</Link>
      </p>
    </AuthForm>
  );
};

export default LoginPage;
