import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import styled, { keyframes, css } from 'styled-components';
import { FiPlus, FiUser, FiLogOut, FiHome, FiTrendingUp, FiSettings, FiMenu, FiX } from 'react-icons/fi';
import { FaLaughSquint } from 'react-icons/fa';
import { useTheme } from '../../styles/ThemeContext';
import { FiMoon, FiSun } from 'react-icons/fi';
import NotificationDropdown from '../NotificationDropdown/NotificationDropdown';
// Animations
const fadeIn = keyframes`
  from { opacity: 0; transform: translateY(-10px); }
  to { opacity: 1; transform: translateY(0); }
`;

const pulse = keyframes`
  0% { transform: scale(1); }
  50% { transform: scale(1.05); }
  100% { transform: scale(1); }
`;

// Styled Components
const NavbarContainer = styled.div`
  width: 100%;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 1000;
`;

const Nav = styled.nav`
  background: linear-gradient(135deg, rgba(255, 115, 0, 0.83) 0%, rgb(230, 65, 5) 100%);
  padding: 0.8rem 2rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.15);
  transition: transform 0.3s cubic-bezier(0.25, 0.8, 0.5, 1), background 0.3s ease;
  transform: translateY(0);

  ${({ $hidden }) => $hidden && css`
    transform: translateY(-100%);
  `}

  ${({ $scrolled }) => $scrolled && css`
    background: rgba(230, 65, 5, 0.98);
    box-shadow: 0 2px 15px rgba(0, 0, 0, 0.2);
  `}

  @media (max-width: 768px) {
    padding: 0.8rem 1.5rem;
  }
`;

const NavbarPlaceholder = styled.div`
  height: ${({ $height }) => $height}px;
  transition: height 0.3s ease;
`;

const Logo = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-size: 1.5rem;
  font-weight: bold;
  color: white;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    opacity: 0.9;
    transform: scale(1.03);
  }

  @media (max-width: 768px) {
    font-size: 1.3rem;
  }
`;

const LogoIcon = styled(FaLaughSquint)`
  animation: ${pulse} 2s infinite;
`;

