const Navbar = () => {
  const { theme, toggleTheme } = useTheme();
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [activeButton, setActiveButton] = useState('home');
  const [hidden, setHidden] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const lastScrollY = useRef(0);
  const navRef = useRef(null);
  const [navbarHeight, setNavbarHeight] = useState(0);

  // Calculate navbar height
  useEffect(() => {
    if (navRef.current) {
      setNavbarHeight(navRef.current.offsetHeight);
    }
  }, []);

  // Scroll handler for hide/show navbar
  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;

      if (currentScrollY > 100) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }

      if (currentScrollY > lastScrollY.current && currentScrollY > 100) {
        // Scrolling down
        setHidden(true);
      } else if (currentScrollY < lastScrollY.current) {
        // Scrolling up
        setHidden(false);
      }

      lastScrollY.current = currentScrollY;
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = () => {
      if (dropdownOpen) setDropdownOpen(false);
    };
    document.addEventListener('click', handleClickOutside);
    return () => document.removeEventListener('click', handleClickOutside);
  }, [dropdownOpen]);

  // Close mobile menu on route change
  useEffect(() => {
    setMobileMenuOpen(false);
  }, [location]);

  const handleLogout = async () => {
    try {
      await logout();
      navigate('/');
    } catch (error) {
      console.error('Logout error:', error);
    }
    setDropdownOpen(false);
  };

  const toggleDropdown = (e) => {
    e.stopPropagation();
    setDropdownOpen(!dropdownOpen);
  };

  const toggleMobileMenu = () => {
    setMobileMenuOpen(!mobileMenuOpen);
  };

  const handleButtonClick = (buttonName) => {
    setActiveButton(buttonName);
  };

  return (
    <>
      <NavbarContainer>
        <Nav 
          ref={navRef}
          $hidden={hidden} 
          $scrolled={scrolled}
        >
          <Link to="/" style={{ textDecoration: 'none' }}>
            <Logo>
              <LogoIcon />
              <span>iFuunny</span>
            </Logo>
          </Link>

          <MobileMenuButton onClick={toggleMobileMenu}>
            {mobileMenuOpen ? <FiX /> : <FiMenu />}
          </MobileMenuButton>

          <NavLinks $mobileMenuOpen={mobileMenuOpen}>
            {user ? (
              <>
                <ThemeToggleButton onClick={toggleTheme}>
                  {theme === 'light' ? <FiMoon size={20} /> : <FiSun size={20} />}
                </ThemeToggleButton>

                {/* Adicione o NotificationDropdown aqui */}
                <NotificationDropdown />

                <ButtonGroup>
                  <NavButton 
                    to="/" 
                    $active={activeButton === 'home'}
                    onClick={() => handleButtonClick('home')}
                  >
                    <FiTrendingUp />
                    <span>Destaques</span>
                  </NavButton>
                  <NavButton 
                    to="/feed" 
                    $active={activeButton === 'feed'}
                    onClick={() => handleButtonClick('feed')}
                  >
                    <FiHome />
                    <span>coletivo</span>
                  </NavButton>
                </ButtonGroup>

                <NavButton 
                  to="/upload" 
                  $active={activeButton === 'upload'}
                  onClick={() => handleButtonClick('upload')}
                >
                  <FiPlus />
                  <span>Criar</span>
                </NavButton>

                <UserDropdown>
                  <DropdownButton onClick={toggleDropdown}>
                    <UserAvatar
                      src={
                        user.profile?.avatar
                          ? user.profile.avatar.startsWith('http')
                            ? user.profile.avatar
                            : `${process.env.REACT_APP_API_URL || 'https://api.jokesteronline.org'}${user.profile.avatar}`
                          : 'https://i.pravatar.cc/150?img=3'
                      }
                      alt={user.username}
                      crossOrigin="anonymous"
                      onError={(e) => {
                        e.target.src = 'https://i.pravatar.cc/150?img=3';
                        e.target.onerror = null;
                      }}
                    />
                  </DropdownButton>

                  <DropdownMenu $isOpen={dropdownOpen}>
                    <DropdownItem to={`/users/${user._id}`}>
                      <FiUser />
                      <span>Meu Perfil</span>
                    </DropdownItem>
                    <DropdownItem to="/settings">
                      <FiSettings />
                      <span>Configurações</span>
                    </DropdownItem>
                    <DropdownDivider />
                    <DropdownItem as="button" onClick={handleLogout}>
                      <FiLogOut />
                      <span>Sair</span>
                    </DropdownItem>
                  </DropdownMenu>
                </UserDropdown>
              </>
            ) : (
              <>
                <NavButton to="/login">
                  <span>Login</span>
                </NavButton>
                <NavButton to="/register">
                  <span>Registrar</span>
                </NavButton>
              </>
            )}
          </NavLinks>
        </Nav>
      </NavbarContainer>
      <NavbarPlaceholder $height={navbarHeight} />
    </>
  );
};

export default Navbar;
