import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import api from '../../services/api';
import { useParams } from 'react-router-dom';
import MemeCard from '../MemeCard';
import { PulseLoader } from 'react-spinners'; // Adicione esta dependência
import { FaSadTear, FaSmileWink } from 'react-icons/fa'; // Adicione esta dependência

const Container = styled.div`
  max-width: 1400px;
  margin: 0 auto;
  padding: 20px;
`;

const MemeGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
  gap: 30px;
  padding: 20px;

  @media (max-width: 768px) {
    grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
    gap: 20px;
  }

  @media (max-width: 480px) {
    grid-template-columns: 1fr;
    gap: 15px;
  }
`;

const StatusMessage = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 300px;
  text-align: center;
  padding: 40px;
  background: ${({ theme }) => theme.cardBg || '#f8f9fa'};
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.05);
  color: ${({ theme }) => theme.text || '#333'};

  svg {
    font-size: 3rem;
    margin-bottom: 20px;
    color: ${({ theme }) => theme.primary || '#6c5ce7'};
  }

  h3 {
    font-size: 1.5rem;
    margin-bottom: 10px;
  }

  p {
    font-size: 1rem;
    opacity: 0.8;
  }
`;

const Title = styled.h2`
  font-size: 2rem;
  color: ${({ theme }) => theme.text || '#333'};
  margin-bottom: 20px;
  padding-left: 20px;
  position: relative;

  &::before {
    content: '';
    position: absolute;
    left: 0;
    top: 50%;
    transform: translateY(-50%);
    height: 70%;
    width: 5px;
    background: ${({ theme }) => theme.primary || '#6c5ce7'};
    border-radius: 5px;
  }
`;

const UserMemes = () => {
  const { userId } = useParams();
  const [memes, setMemes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUserMemes = async () => {
      try {
        setLoading(true);
        const { data } = await api.get(`/users/${userId}/memes`);
        setMemes(data);
      } catch (err) {
        setError('Falha ao carregar memes');
        console.error('Erro ao buscar memes:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchUserMemes();
  }, [userId]);

  if (loading) return (
    <Container>
      <StatusMessage>
        <PulseLoader color="#6c5ce7" size={15} />
        <h3>Carregando memes...</h3>
        <p>Preparando as melhores piadas pra você</p>
      </StatusMessage>
    </Container>
  );

  if (error) return (
    <Container>
      <StatusMessage>
        <FaSadTear />
        <h3>Oops! Algo deu errado</h3>
        <p>{error}</p>
        <p>Tente recarregar a página</p>
      </StatusMessage>
    </Container>
  );

  if (memes.length === 0) return (
    <Container>
      <StatusMessage>
        <FaSmileWink />
        <h3>Nenhum meme encontrado</h3>
        <p>Parece que este usuário ainda não criou memes</p>
      </StatusMessage>
    </Container>
  );

  return (
    <Container>
      <Title>Post</Title>
      <MemeGrid>
        {memes.map(meme => (
          <MemeCard key={meme._id} meme={meme} />
        ))}
      </MemeGrid>
    </Container>
  );
};

export default UserMemes;
