// src/components/profile/ProfileHeader.jsx
import styled from 'styled-components';
import { FiEdit } from 'react-icons/fi';

const HeaderContainer = styled.div`
  position: relative;
  margin-bottom: 30px;
`;

const CoverPhoto = styled.div`
  height: 300px;
  background-image: url(${props => props.src});
  background-size: cover;
  background-position: center;
  border-radius: 8px;
  position: relative;
`;

const ProfileInfo = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-top: -75px;
  position: relative;
  z-index: 1;
`;

const Avatar = styled.img`
  width: 150px;
  height: 150px;
  border-radius: 50%;
  border: 4px solid white;
  object-fit: cover;
`;

const Username = styled.h1`
  margin-top: 15px;
  font-size: 24px;
`;

const Bio = styled.p`
  margin-top: 10px;
  color: #666;
  max-width: 500px;
  text-align: center;
`;

const StatsContainer = styled.div`
  display: flex;
  gap: 30px;
  margin-top: 20px;
`;

const StatItem = styled.div`
  text-align: center;
`;

const StatNumber = styled.div`
  font-weight: bold;
  font-size: 18px;
`;

const StatLabel = styled.div`
  font-size: 14px;
  color: #666;
`;

const EditButton = styled.button`
  position: absolute;
  top: 20px;
  right: 20px;
  background: rgba(0,0,0,0.5);
  color: white;
  border: none;
  padding: 8px 15px;
  border-radius: 20px;
  display: flex;
  align-items: center;
  gap: 5px;
  cursor: pointer;
  transition: all 0.2s;
  
  &:hover {
    background: rgba(0,0,0,0.7);
  }
`;

const ProfileHeader = ({ user }) => {
  return (
    <HeaderContainer>
      <CoverPhoto src={user.profile.coverPhoto || '/default-cover.jpg'} />
      <ProfileInfo>
        <Avatar src={user.profile.avatar || '/default-avatar.jpg'} />
        <Username>@{user.username}</Username>
        <Bio>{user.profile.bio || 'No bio yet'}</Bio>
        <StatsContainer>
          <StatItem>
            <StatNumber>{user.stats.memeCount}</StatNumber>
            <StatLabel>Posts</StatLabel>
          </StatItem>
          <StatItem>
            <StatNumber>{user.stats.followersCount}</StatNumber>
            <StatLabel>Followers</StatLabel>
          </StatItem>
          <StatItem>
            <StatNumber>{user.stats.followingCount}</StatNumber>
            <StatLabel>Following</StatLabel>
          </StatItem>
        </StatsContainer>
      </ProfileInfo>
      <EditButton>
        <FiEdit /> Edit Profile
      </EditButton>
    </HeaderContainer>
  );
};

export default ProfileHeader;
