import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import { FiMessageSquare, FiShare2, FiRepeat } from 'react-icons/fi';
import { FaSmile, FaAngry, FaCheck } from 'react-icons/fa';
import api from '../services/api';
import { useAuth } from '../contexts/AuthContext';
import CommentSection from './CommentSection';
import { useNavigate } from 'react-router-dom';


// Animations
const popIn = keyframes`
  0% { transform: scale(0.5); opacity: 0; }
  70% { transform: scale(1.2); opacity: 0.7; }
  100% { transform: scale(1); opacity: 1; }
`;

const pulse = keyframes`
  0% { transform: scale(1); }
  50% { transform: scale(1.1); }
  100% { transform: scale(1); }
`;

const spin = keyframes`
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
`;

const floating = keyframes`
  0% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
  100% { transform: translateY(0px); }
`;

const gradientBackground = keyframes`
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
`;

const Card = styled.div`
  background: linear-gradient(145deg, #ffffff, #f8f8f8);
  border-radius: 20px;
  margin-bottom: 24px;
  box-shadow: 0 8px 32px rgba(0,0,0,0.1);
  overflow: hidden;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  position: relative;
  border: 1px solid rgba(255,255,255,0.3);
  
  &:hover {
    transform: translateY(-8px) rotateZ(${Math.random() * 2 - 1}deg);
    box-shadow: 0 12px 40px rgba(255, 69, 0, 0.2);
  }
`;

const RepostBadge = styled.div`
  position: absolute;
  top: 10px;
  left: 10px;
  background: linear-gradient(45deg, #10b981, #059669);
  color: white;
  padding: 6px 12px;
  border-radius: 20px;
  font-size: 0.8rem;
  display: flex;
  align-items: center;
  z-index: 1;
  box-shadow: 0 2px 8px rgba(16, 185, 129, 0.3);
  text-shadow: 0 1px 2px rgba(0,0,0,0.2);
  animation: ${floating} 3s ease-in-out infinite;
  
  svg {
    margin-right: 4px;
  }
`;

const Header = styled.div`
  display: flex;
  align-items: center;
  padding: 16px;
  background: linear-gradient(to right, #fafafa, #f5f5f5);
  position: relative;
  border-bottom: 1px solid rgba(0,0,0,0.05);
`;

const Avatar = styled.img`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  margin-right: 12px;
  object-fit: cover;
  border: 3px solid #ff4500;
  transition: all 0.3s;
  position: relative;
  overflow: hidden;
  cursor: pointer;
  
  &::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: linear-gradient(45deg, transparent, rgba(255,255,255,0.8), transparent);
    transform: rotate(45deg);
    animation: ${gradientBackground} 3s infinite linear;
  }
  
  &:hover {
    transform: rotate(15deg) scale(1.1);
    border-color: #ff6b45;
  }
`;

const Username = styled.span`
  font-weight: 700;
  font-size: 1rem;
  color: #333;
  transition: all 0.2s;
  cursor: pointer;
  position: relative;
  
  &::after {
    content: '';
    position: absolute;
    bottom: -2px;
    left: 0;
    width: 0;
    height: 2px;
    background: #ff4500;
    transition: width 0.3s;
  }
  
  &:hover {
    color: #ff4500;
    
    &::after {
      width: 100%;
    }
  }
`;

const MediaContainer = styled.div`
  width: 100%;
  min-height: 300px;
  background: linear-gradient(45deg, #f0f0f0, #fafafa);
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
  overflow: hidden;
  
  img, video {
    max-width: 100%;
    max-height: 500px;
    object-fit: contain;
    transition: all 0.4s;
    border-radius: 12px;
    z-index: 1;
    
    &:hover {
      transform: scale(1.03);
      filter: saturate(1.2) brightness(1.05);
    }
  }
`;

const Caption = styled.p`
  padding: 16px;
  margin: 0;
  font-size: 0.95rem;
  line-height: 1.5;
  color: #333;
  background: white;
  position: relative;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 4px;
    height: 100%;
    background: linear-gradient(to bottom, #ff4500, #ff6b45);
  }
`;

const Tags = styled.div`
  padding: 0 16px 12px;
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
  background: white;
`;

const Tag = styled.span`
  color: white;
  font-size: 0.85rem;
  background: linear-gradient(45deg, #ff4500, #ff6b45);
  padding: 6px 12px;
  border-radius: 20px;
  transition: all 0.3s;
  text-shadow: 0 1px 2px rgba(0,0,0,0.2);
  cursor: pointer;
  
  &:hover {
    transform: translateY(-3px) scale(1.05);
    box-shadow: 0 4px 12px rgba(255, 69, 0, 0.3);
  }
`;

const Actions = styled.div`
  display: flex;
  padding: 12px 16px;
  border-top: 1px solid rgba(0,0,0,0.05);
  background: linear-gradient(to right, #fafafa, #f5f5f5);
  justify-content: space-between;
`;

const LeftActions = styled.div`
  display: flex;
  gap: 8px;
`;

const RightActions = styled.div`
  display: flex;
  gap: 8px;
`;

const ActionButton = styled.button`
  background: none;
  border: none;
  display: flex;
  align-items: center;
  color: ${props => props.$active ? props.$color || '#ff4500' : '#666'};
  cursor: pointer;
  font-size: 0.9rem;
  padding: 8px 16px;
  border-radius: 12px;
  transition: all 0.3s;
  position: relative;
  overflow: hidden;
  
  svg {
    margin-right: 6px;
    font-size: 1.2rem;
    transition: transform 0.3s;
  }

  &:hover {
    background: rgba(0,0,0,0.05);
    color: ${props => props.$color || '#ff4500'};
    transform: translateY(-2px);
    
    svg {
      transform: scale(1.2);
    }
  }
  
  &::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 0;
    height: 0;
    background: rgba(255,255,255,0.2);
    border-radius: 50%;
    transform: translate(-50%, -50%);
    transition: width 0.3s, height 0.3s;
  }
  
  &:hover::after {
    width: 120%;
    height: 120%;
  }
  
  &:active {
    transform: scale(0.95) translateY(-2px);
  }
`;

const RepostButton = styled(ActionButton)`
  color: ${props => props.$active ? '#10b981' : '#666'};
  
  &:hover {
    color: #10b981;
  }
`;

const ReactionAnimation = styled.div`
  position: absolute;
  animation: ${popIn} 0.5s forwards, ${pulse} 0.5s 0.5s forwards;
  font-size: 2.5rem;
  opacity: 0;
  pointer-events: none;
  filter: drop-shadow(0 4px 8px rgba(0,0,0,0.2));
  text-shadow: 0 2px 4px rgba(0,0,0,0.3);
  z-index: 10;
`;
const playPulse = keyframes`
  0% { transform: scale(1); opacity: 0.8; }
  50% { transform: scale(1.1); opacity: 1; }
  100% { transform: scale(1); opacity: 0.8; }
`;

const fadeIn = keyframes`
  from { opacity: 0; }
  to { opacity: 1; }
`;

// Estilo para o container de vídeo customizado
const VideoContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  overflow: hidden;
  border-radius: 12px;
`;

// Estilo para o vídeo sem controles nativos
const CustomVideo = styled.video`
  max-width: 100%;
  max-height: 500px;
  object-fit: contain;
  border-radius: 12px;
  z-index: 1;
  transition: all 0.4s;
  
  &:hover {
    transform: scale(1.03);
    filter: saturate(1.2) brightness(1.05);
  }
`;

// Botão de play customizado
const PlayButton = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 80px;
  height: 80px;
  background: rgba(255, 69, 0, 0.8);
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 2;
  animation: ${playPulse} 2s infinite;
  transition: all 0.3s;
  backdrop-filter: blur(2px);
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.3);
  
  &::before {
    content: '';
    position: absolute;
    width: 0;
    height: 0;
    border-top: 15px solid transparent;
    border-bottom: 15px solid transparent;
    border-left: 25px solid white;
    margin-left: 5px;
  }
  
  &:hover {
    background: rgba(255, 69, 0, 0.9);
    transform: translate(-50%, -50%) scale(1.1);
  }
`;

// Controles customizados do vídeo
const VideoControls = styled.div`
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  background: linear-gradient(to top, rgba(0,0,0,0.7), transparent);
  padding: 10px;
  display: flex;
  align-items: center;
  z-index: 3;
  opacity: 0;
  transition: opacity 0.3s;
  
  ${VideoContainer}:hover & {
    opacity: 1;
    animation: ${fadeIn} 0.3s forwards;
  }
`;

const ControlButton = styled.button`
  background: none;
  border: none;
  color: white;
  font-size: 1.2rem;
  margin: 0 5px;
  cursor: pointer;
  transition: all 0.2s;
  
  &:hover {
    color: #ff4500;
    transform: scale(1.2);
  }
`;

const ProgressBar = styled.div`
  flex-grow: 1;
  height: 4px;
  background: rgba(255,255,255,0.3);
  border-radius: 2px;
  margin: 0 10px;
  position: relative;
  overflow: hidden;
`;

const Progress = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  width: ${props => props.$progress || '0%'};
  background: #ff4500;
  border-radius: 2px;
`;

const TimeDisplay = styled.span`
  color: white;
  font-size: 0.8rem;
  min-width: 80px;
  text-align: center;
`;

const MemeCard = ({ meme, isRepost = false }) => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [isLiked, setIsLiked] = useState(false);
  const [likeCount, setLikeCount] = useState(meme.likes?.length || 0);
  const [showComments, setShowComments] = useState(false);
  const [commentCount, setCommentCount] = useState(meme.commentCount || 0);
  const [reaction, setReaction] = useState(null);
  const [isReposted, setIsReposted] = useState(false);
  const [repostCount, setRepostCount] = useState(meme.repostCount || 0);
  const [isCheckingRepost, setIsCheckingRepost] = useState(true);

  useEffect(() => {
    if (user && meme.likes) {
      setIsLiked(meme.likes.some(like => like.toString() === user._id));
    }
    setLikeCount(meme.likes?.length || 0);
    setCommentCount(meme.commentCount || 0);
    setRepostCount(meme.repostCount || 0);
    
    if (user) {
      checkIfReposted();
    } else {
      setIsCheckingRepost(false);
    }
  }, [meme, user]);

  const checkIfReposted = async () => {
    try {
      // Verifica se o meme atual é um repost do usuário
      if (meme.originalMeme && meme.author._id === user._id) {
        setIsReposted(true);
      } else {
        // Ou verifica se o usuário está na lista de reposts
        setIsReposted(meme.reposts?.some(repost => repost.toString() === user._id));
      }
    } catch (error) {
      console.error('Erro ao verificar repost:', error);
    } finally {
      setIsCheckingRepost(false);
    }
  };
  const handleLike = async () => {
    if (!user) return;
    
    try {
      const response = await api.post(`/memes/${meme._id}/like`);
      setIsLiked(response.data.isLiked);
      setLikeCount(response.data.likeCount);
      setReaction(response.data.isLiked ? 'like' : 'dislike');
      setTimeout(() => setReaction(null), 1500);
    } catch (error) {
      console.error('Erro ao curtir:', error);
    }
  };

  const handleDislike = async () => {
    if (!user || !isLiked) return;
    
    try {
      const response = await api.post(`/memes/${meme._id}/like`);
      setIsLiked(false);
      setLikeCount(response.data.likeCount);
      setReaction('dislike');
      setTimeout(() => setReaction(null), 1500);
    } catch (error) {
      console.error('Erro ao remover curtida:', error);
    }
  };

  const handleRepost = async () => {
    if (!user) return;
    
    // Verifica se o usuário é o autor original do meme
    if (meme.author?._id === user._id) {
      // Mostra feedback visual que não pode repostar próprio conteúdo
      setReaction('self-repost');
      setTimeout(() => setReaction(null), 1500);
      return;
    }
    
    if (isReposted || isRepost) return;

    try {
      const response = await api.post(`/memes/${meme._id}/repost`);
      setIsReposted(true);
      setRepostCount(response.data.repostCount);
      
      setReaction('repost');
      setTimeout(() => setReaction(null), 1500);
    } catch (error) {
      console.error('Erro ao repostar:', error);
      if (error.response && error.response.status === 409) {
        setIsReposted(true);
      }
    }
  };

  const handleCommentSubmit = async (formData) => {
    try {
      // Debug: Mostrar conteúdo do FormData
      for (let [key, value] of formData.entries()) {

      }
  
      const response = await api.post(
        `/memes/${meme._id}/comments`,
        formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        }
      );
  
      // Atualiza a contagem de comentários
      const updatedMeme = await api.get(`/memes/${meme._id}`);
      setCommentCount(updatedMeme.data.commentCount || 0);
      
      return response.data;
    } catch (error) {
      console.error('Erro detalhado:', {
        message: error.message,
        response: error.response?.data,
        status: error.response?.status,
        config: error.config // Adiciona informações da requisição
      });
  
      throw new Error(error.response?.data?.message || error.message || 'Erro ao enviar comentário');
    }
  };

  const buildUrl = (url) => {
    if (!url) return '';
    
    // Se já for uma URL completa, retorna diretamente
    if (url.startsWith('http')) return url;
    
    // Se começar com /uploads, assume que é um caminho relativo
    if (url.startsWith('/uploads/')) {
      return `${window.location.origin}${url}`;
    }
    
    // Padrão para outras URLs da API
    return `${process.env.REACT_APP_API_URL || 'http://localhost:5000'}${url}`;
  };

  return (
    <Card>
      {isRepost && (
        <RepostBadge>
          <FiRepeat /> Repostado por @{meme.repostedBy?.username || 'usuário'}
        </RepostBadge>
      )}
      
      {reaction && (
        <ReactionAnimation style={{
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          color: reaction === 'like' ? '#ff4500' : 
                reaction === 'dislike' ? '#333' : 
                reaction === 'repost' ? '#10b981' : '#ff4500'
        }}>
          {reaction === 'like' ? <FaSmile /> : 
           reaction === 'dislike' ? <FaAngry /> : 
           reaction === 'repost' ? <FaCheck /> : 
           <span style={{ fontSize: '1rem' }}>Você é o autor!</span>}
        </ReactionAnimation>
      )}
      
      <Header>
        <Avatar 
          src={
            meme.author?.profile?.avatar
              ? buildUrl(meme.author.profile.avatar)
              : 'https://i.pravatar.cc/150?img=3'
          }
          alt={meme.author?.username}
          onClick={() => meme.author && navigate(`/users/${meme.author._id}`)}
          crossOrigin="anonymous"
          onError={(e) => {
            e.target.src = 'https://i.pravatar.cc/150?img=3';
            e.target.onerror = null;
          }}
        />
        <Username 
          onClick={() => meme.author && navigate(`/users/${meme.author._id}`)}
        >
          @{meme.author?.username || 'usuário desconhecido'}
        </Username>
      </Header>
      
      <MediaContainer>
        {meme.mediaType === 'image' ? (
          <img 
            src={buildUrl(meme.mediaUrl)}
            alt={meme.caption || 'Meme image'}
            style={{
              transform: showComments ? 'scale(0.98)' : 'scale(1)',
              filter: showComments ? 'brightness(0.95)' : 'none'
            }}
            crossOrigin="anonymous"
            onError={(e) => {
              e.target.src = 'https://placehold.co/600x400?text=Imagem+não+carregada';
              e.target.onerror = null;
            }}
          />
        ) : (
          <video 
            src={buildUrl(meme.mediaUrl)}
            controls 
            style={{
              borderRadius: '16px',
              boxShadow: '0 8px 24px rgba(0,0,0,0.1)'
            }}
            crossOrigin="anonymous"
          />
        )}
        <div style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: `linear-gradient(45deg, 
            rgba(255,255,255,0.1), 
            rgba(255,69,0,0.05))`,
          pointerEvents: 'none'
        }}/>
      </MediaContainer>

      {meme.caption && <Caption>{meme.caption}</Caption>}
      
      {meme.tags?.length > 0 && (
        <Tags>
          {meme.tags.map(tag => (
            <Tag key={tag} onClick={() => navigate(`/explore?tag=${tag}`)}>#{tag}</Tag>
          ))}
        </Tags>
      )}

      <Actions>
        <LeftActions>
          <ActionButton 
            onClick={handleLike} 
            $active={isLiked}
            $color="#ff4500"
          >
            <FaSmile /> {likeCount}
          </ActionButton>
          <ActionButton 
            onClick={handleDislike} 
            $active={!isLiked && likeCount > 0}
            $color="#333"
          >
            <FaAngry /> 
          </ActionButton>
          <ActionButton 
            onClick={() => setShowComments(!showComments)}
            $active={showComments}
          >
            <FiMessageSquare /> {commentCount}
          </ActionButton>
        </LeftActions>
        
        <RightActions>
        <RepostButton 
          onClick={handleRepost}
          $active={isReposted}
          disabled={isReposted || isCheckingRepost || isRepost || (meme.author?._id === user?._id)}
          title={meme.author?._id === user?._id ? "Você não pode repostar seu próprio conteúdo" : ""}
        >
          {isCheckingRepost ? (
            <FiRepeat />
          ) : isReposted ? (
            <FaCheck />
          ) : (
            <FiRepeat />
          )} {repostCount}
        </RepostButton>
        <ActionButton>
          <FiShare2 />
        </ActionButton>
      </RightActions>
      </Actions>

      {showComments && (
        <CommentSection 
          memeId={meme._id}
          onCommentSubmit={handleCommentSubmit}
        />
      )}
    </Card>
  );
};

export default MemeCard;
