// src/pages/ProfilePage.jsx
import { useState, useEffect } from 'react';
import { useParams, Navigate } from 'react-router-dom';
import styled from 'styled-components';
import api from '../services/api';
import ProfileHeader from '../components/profile/ProfileHeader';
import ProfileTabs from '../components/profile/ProfileTabs';
import UserMemes from '../components/profile/UserMemes';
import UserLikes from '../components/profile/UserLikes';
import UserActivity from '../components/profile/UserActivity';
import UserGuests from '../components/profile/UserGuests';
import ProfileSettings from '../components/profile/ProfileSettings';

const ProfileContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
`;

const ProfilePage = () => {
  const { userId } = useParams();
  const [user, setUser] = useState(null);
  const [activeTab, setActiveTab] = useState('memes');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        setLoading(true);
        setError(null);
        const { data } = await api.get(`/users/${userId}`);
        setUser(data);
      } catch (error) {
        console.error('Error fetching user:', error);
        setError(error);
      } finally {
        setLoading(false);
      }
    };

    fetchUser();
  }, [userId]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error loading profile</div>;
  if (!user && !loading) return <div>User not found</div>; // Removi o Navigate

  return (
    <ProfileContainer>
      <ProfileHeader user={user} />
      <ProfileTabs activeTab={activeTab} setActiveTab={setActiveTab} />
      
      {activeTab === 'memes' && <UserMemes userId={userId} />}
      {activeTab === 'likes' && <UserLikes userId={userId} />}
      {activeTab === 'activity' && <UserActivity userId={userId} />}
      {activeTab === 'guests' && <UserGuests userId={userId} />}
      {activeTab === 'settings' && <ProfileSettings user={user} />}
    </ProfileContainer>
  );
};

export default ProfilePage;
