// ============ Componente Principal ============
const CommentSection = ({ memeId, onCommentSubmit }) => {
  const { user } = useAuth();
  const [comments, setComments] = useState([]);
  const [commentText, setCommentText] = useState('');
  const [loading, setLoading] = useState(true);
  const [replyingTo, setReplyingTo] = useState(null);
  const [replyText, setReplyText] = useState('');
  const [editingId, setEditingId] = useState(null);
  const [editText, setEditText] = useState('');
  const [showOptions, setShowOptions] = useState(null);
  const [parentCommentId, setParentCommentId] = useState(null);

  // Buscar comentários
  useEffect(() => {
    const fetchComments = async () => {
      try {
        setLoading(true);
        const response = await api.get(`/memes/${memeId}/comments`);
        setComments(response.data.comments);
      } catch (error) {
        console.error('Erro ao buscar comentários:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchComments();
  }, [memeId]);

  // Adicionar novo comentário
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!commentText.trim()) return;

    try {
      await onCommentSubmit(commentText);
      setCommentText('');
      const response = await api.get(`/memes/${memeId}/comments`);
      setComments(response.data.comments);
    } catch (error) {
      console.error('Erro ao enviar comentário:', error);
    }
  };

  // Adicionar resposta (para comentários ou outras respostas)
  const handleReplySubmit = async (e, replyToId, isCommentLevel = true) => {
    e.preventDefault();
    if (!replyText.trim()) return;

    try {
      let endpoint;
      if (isCommentLevel) {
        endpoint = `/memes/${memeId}/comments/${replyToId}/replies`;
      } else {
        endpoint = `/memes/${memeId}/comments/${parentCommentId}/replies/${replyToId}/replies`;
      }

      const response = await api.post(endpoint, { text: replyText });

      const updatedComments = comments.map(comment => {
        // Atualiza comentário principal
        if (isCommentLevel && comment._id === replyToId) {
          return {
            ...comment,
            replies: [...(comment.replies || []), response.data.reply]
          };
        }
        
        // Atualiza respostas aninhadas
        if (!isCommentLevel) {
          const updateNestedReplies = (replies) => {
            return replies.map(reply => {
              if (reply._id === replyToId) {
                return {
                  ...reply,
                  replies: [...(reply.replies || []), response.data.reply]
                };
              }
              if (reply.replies?.length) {
                return {
                  ...reply,
                  replies: updateNestedReplies(reply.replies)
                };
              }
              return reply;
            });
          };
          
          return {
            ...comment,
            replies: updateNestedReplies(comment.replies || [])
          };
        }
        
        return comment;
      });

      setComments(updatedComments);
      setReplyText('');
      setReplyingTo(null);
      setParentCommentId(null);
    } catch (error) {
      console.error('Erro ao enviar resposta:', error);
    }
  };

  // Editar comentário/resposta
  const handleEdit = (comment) => {
    setEditingId(comment._id);
    setEditText(comment.text);
    setShowOptions(null);
  };

  const handleSaveEdit = async (commentId, isReply = false, parentId = null) => {
    try {
      let endpoint;
      if (isReply && parentId) {
        endpoint = `/memes/${memeId}/comments/${parentId}/replies/${commentId}`;
      } else {
        endpoint = `/memes/${memeId}/comments/${commentId}`;
      }

      const response = await api.put(endpoint, { text: editText });

      const updatedComments = comments.map(comment => {
        if (isReply) {
          // Atualizando uma resposta
          if (comment._id === parentId) {
            const updatedReplies = comment.replies?.map(reply => {
              if (reply._id === commentId) {
                return {
                  ...reply,
                  text: response.data.reply.text,
                  isEdited: true,
                  updatedAt: response.data.reply.updatedAt
                };
              }
              return reply;
            });
            return { ...comment, replies: updatedReplies || [] };
          }
          
          // Atualizando respostas aninhadas
          const updateNestedReplies = (replies) => {
            return replies.map(reply => {
              if (reply._id === commentId) {
                return {
                  ...reply,
                  text: response.data.reply.text,
                  isEdited: true,
                  updatedAt: response.data.reply.updatedAt
                };
              }
              if (reply.replies?.length) {
                return {
                  ...reply,
                  replies: updateNestedReplies(reply.replies)
                };
              }
              return reply;
            });
          };
          
          return {
            ...comment,
            replies: updateNestedReplies(comment.replies || [])
          };
        } else {
          // Atualizando comentário principal
          if (comment._id === commentId) {
            return {
              ...comment,
              text: response.data.comment.text,
              isEdited: true,
              updatedAt: response.data.comment.updatedAt
            };
          }
          return comment;
        }
      });

      setComments(updatedComments);
      setEditingId(null);
      setEditText('');
    } catch (error) {
      console.error('Erro ao editar:', error);
    }
  };
  
  // Excluir comentário/resposta
  const handleDelete = async (commentId, isReply = false, parentId = null) => {
    if (!window.confirm('Tem certeza que deseja excluir?')) return;
    
    try {
      let endpoint;
      if (isReply && parentId) {
        endpoint = `/memes/${memeId}/comments/${parentId}/replies/${commentId}`;
      } else {
        endpoint = `/memes/${memeId}/comments/${commentId}`;
      }

      await api.delete(endpoint);

      const updatedComments = comments.map(comment => {
        if (isReply) {
          // Excluindo uma resposta
          if (comment._id === parentId) {
            const updatedReplies = comment.replies?.map(reply => {
              if (reply._id === commentId) {
                return {
                  ...reply,
                  isDeleted: true,
                  text: "[Resposta removida]",
                  user: { 
                    _id: 'deleted',
                    username: "[Removido]",
                    profile: { avatar: 'https://i.pravatar.cc/150?img=3' }
                  }
                };
              }
              return reply;
            });
            return { ...comment, replies: updatedReplies || [] };
          }
          
          // Excluindo respostas aninhadas
          const updateNestedReplies = (replies) => {
            return replies.map(reply => {
              if (reply._id === commentId) {
                return {
                  ...reply,
                  isDeleted: true,
                  text: "[Resposta removida]",
                  user: { 
                    _id: 'deleted',
                    username: "[Removido]",
                    profile: { avatar: 'https://i.pravatar.cc/150?img=3' }
                  }
                };
              }
              if (reply.replies?.length) {
                return {
                  ...reply,
                  replies: updateNestedReplies(reply.replies)
                };
              }
              return reply;
            });
          };
          
          return {
            ...comment,
            replies: updateNestedReplies(comment.replies || [])
          };
        } else {
          // Excluindo comentário principal
          if (comment._id === commentId) {
            return {
              ...comment,
              isDeleted: true,
              text: "[Comentário removido]",
              user: { 
                _id: 'deleted',
                username: "[Removido]",
                profile: { avatar: 'https://i.pravatar.cc/150?img=3' }
              }
            };
          }
          return comment;
        }
      });

      setComments(updatedComments);
      setShowOptions(null);
    } catch (error) {
      console.error('Erro ao excluir:', error);
    }
  };

  // Reações
  const handleReaction = async (commentId, reaction, isReply = false, parentId = null) => {
    try {
      let endpoint;
      if (isReply && parentId) {
        endpoint = `/memes/${memeId}/comments/${parentId}/replies/${commentId}/reaction`;
      } else {
        endpoint = `/memes/${memeId}/comments/${commentId}/reaction`;
      }

      const response = await api.post(endpoint, { reaction });

      const updatedComments = comments.map(comment => {
        if (isReply) {
          // Atualizando reação em resposta
          if (comment._id === parentId) {
            const updatedReplies = comment.replies?.map(reply => {
              if (reply._id === commentId) {
                return {
                  ...reply,
                  likes: response.data.likes,
                  dislikes: response.data.dislikes,
                  userReaction: response.data.userReaction
                };
              }
              return reply;
            });
            return { ...comment, replies: updatedReplies || [] };
          }
          
          // Atualizando reação em respostas aninhadas
          const updateNestedReplies = (replies) => {
            return replies.map(reply => {
              if (reply._id === commentId) {
                return {
                  ...reply,
                  likes: response.data.likes,
                  dislikes: response.data.dislikes,
                  userReaction: response.data.userReaction
                };
              }
              if (reply.replies?.length) {
                return {
                  ...reply,
                  replies: updateNestedReplies(reply.replies)
                };
              }
              return reply;
            });
          };
          
          return {
            ...comment,
            replies: updateNestedReplies(comment.replies || [])
          };
        } else {
          // Atualizando reação em comentário principal
          if (comment._id === commentId) {
            return {
              ...comment,
              likes: response.data.likes,
              dislikes: response.data.dislikes,
              userReaction: response.data.userReaction
            };
          }
          return comment;
        }
      });

      setComments(updatedComments);
    } catch (error) {
      console.error('Erro ao reagir:', error);
    }
  };

  // Formatar hora e data
  const formatTime = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString([], { day: '2-digit', month: '2-digit', year: 'numeric' });
  };

  // Componente Comment recursivo
  const Comment = ({ comment, depth = 0, parentCommentId = null }) => {
    const isEditing = editingId === comment._id;
    const isReplying = replyingTo === comment._id;
    const showMenu = showOptions === comment._id;
    const isReply = depth > 0;
    const isDeleted = comment.isDeleted;
    const MAX_DEPTH = 5;

    return (
      <React.Fragment>
        <CommentItem $depth={depth}>
          <CommentAvatar 
            src={comment.user.profile?.avatar || 'https://i.pravatar.cc/150?img=3'} 
            alt={comment.user.username}
            $isReply={isReply}
          />
          <CommentContent $isReply={isReply}>
            <CommentHeader>
              <CommentUser>
                @{comment.user.username}
                {comment.user.isVerified && <UserBadge>Verificado</UserBadge>}
              </CommentUser>
              
              {!isDeleted && user && (comment.user._id === user._id || user.isAdmin) && (
                <MoreOptionsButton onClick={() => setShowOptions(comment._id)}>
                  <FiMoreHorizontal />
                </MoreOptionsButton>
              )}
            </CommentHeader>
            
            {showMenu && (
              <OptionsMenu>
                <OptionButton onClick={() => handleEdit(comment)}>
                  <FiEdit2 size={14} /> Editar
                </OptionButton>
                <OptionButton onClick={() => handleDelete(comment._id, isReply, parentCommentId)}>
                  <FiTrash2 size={14} /> Excluir
                </OptionButton>
                <OptionButton onClick={() => setShowOptions(null)}>
                  <FiX size={14} /> Cancelar
                </OptionButton>
              </OptionsMenu>
            )}
            
            {isEditing ? (
              <EditForm>
                <EditTextarea
                  value={editText}
                  onChange={(e) => setEditText(e.target.value)}
                  autoFocus
                />
                <EditButtons>
                  <CancelButton onClick={() => setEditingId(null)}>
                    Cancelar
                  </CancelButton>
                  <SaveButton onClick={() => handleSaveEdit(comment._id, isReply, parentCommentId)}>
                    Salvar
                  </SaveButton>
                </EditButtons>
              </EditForm>
            ) : (
              <>
                <CommentText $isDeleted={isDeleted}>
                  {isDeleted ? <DeletedMessage>{comment.text}</DeletedMessage> : comment.text}
                </CommentText>
                <CommentTime>
                  {formatDate(comment.createdAt)} às {formatTime(comment.createdAt)}
                  {comment.isEdited && ' (editado)'}
                </CommentTime>
              </>
            )}
            
            {!isDeleted && (
              <ReactionButtons>
                <ReactionButton 
                  onClick={() => handleReaction(
                    comment._id, 
                    'like', 
                    isReply, 
                    parentCommentId
                  )}
                  $active={comment.userReaction === 'like'}
                >
                  {comment.userReaction === 'like' ? <FaThumbsUp /> : <FiThumbsUp />}
                  {comment.likes?.length || 0}
                </ReactionButton>
                <ReactionButton 
                  onClick={() => handleReaction(
                    comment._id, 
                    'dislike', 
                    isReply, 
                    parentCommentId
                  )}
                  $active={comment.userReaction === 'dislike'}
                >
                  {comment.userReaction === 'dislike' ? <FaThumbsDown /> : <FiThumbsDown />}
                  {comment.dislikes?.length || 0}
                </ReactionButton>
                {user && depth < MAX_DEPTH && (
                  <ReplyButton onClick={() => {
                    setReplyingTo(isReplying ? null : comment._id);
                    if (isReply) setParentCommentId(parentCommentId);
                  }}>
                    <FiCornerDownLeft size={14} />
                    Responder
                  </ReplyButton>
                )}
              </ReactionButtons>
            )}
          </CommentContent>
        </CommentItem>

        {isReplying && depth < MAX_DEPTH && (
          <ReplyForm onSubmit={(e) => handleReplySubmit(
            e, 
            comment._id, 
            depth === 0 // isCommentLevel
          )}>
            <CommentInput
              type="text"
              value={replyText}
              onChange={(e) => setReplyText(e.target.value)}
              placeholder="Escreva uma resposta..."
              autoFocus
            />
            <SubmitButton type="submit">
              <FiSend size={18} />
            </SubmitButton>
          </ReplyForm>
        )}

        {comment.replies?.map(reply => (
          <Comment 
            key={reply._id} 
            comment={reply} 
            depth={depth + 1}
            parentCommentId={comment._id}
          />
        ))}
      </React.Fragment>
    );
  };

  // Renderização virtualizada
  const Row = ({ index, style }) => (
    <div style={style}>
      <Comment 
        comment={comments[index]} 
        depth={0}
      />
    </div>
  );

  return (
    <CommentContainer>
      <CommentList>
        {loading ? (
          <LoadingMessage>Carregando comentários...</LoadingMessage>
        ) : comments.length === 0 ? (
          <EmptyMessage>Nenhum comentário ainda. Seja o primeiro!</EmptyMessage>
        ) : (
          <AutoSizer>
            {({ height, width }) => (
              <List
                height={height}
                itemCount={comments.length}
                itemSize={200} // Ajuste conforme necessário
                width={width}
              >
                {Row}
              </List>
            )}
          </AutoSizer>
        )}
      </CommentList>

      {user && (
        <CommentForm onSubmit={handleSubmit}>
          <CommentInput
            type="text"
            value={commentText}
            onChange={(e) => setCommentText(e.target.value)}
            placeholder="Adicione um comentário..."
          />
          <SubmitButton type="submit">
            <FiSend size={18} />
          </SubmitButton>
        </CommentForm>
      )}
    </CommentContainer>
  );
};

export default CommentSection;
