// utils/renderMentions.js
import React from 'react';
import { useNavigate } from 'react-router-dom';

export const renderMentions = (text, navigate) => {
  if (!text) return null;
  
  return text.split(/(@\w+)/g).map((part, i) => {
    if (part.startsWith('@')) {
      const username = part.substring(1);
      return (
        <span 
          key={i} 
          className="mention"
          style={{
            color: 'var(--primary)',
            fontWeight: 500,
            cursor: 'pointer',
            padding: '0 2px',
            borderRadius: '3px',
            backgroundColor: 'rgba(255, 69, 0, 0.1)'
          }}
          onClick={(e) => {
            e.stopPropagation();
            if (navigate) {
              navigate(`/users/${username}`);
            }
          }}
        >
          {part}
        </span>
      );
    }
    return part;
  });
};
