import React from 'react';
import styled from 'styled-components';
import { FiMessageSquare, FiShare2, FiHeart } from 'react-icons/fi';

const Card = styled.div`
  background: #fff;
  border-radius: 12px;
  margin-bottom: 24px;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
  overflow: hidden;
`;

const Header = styled.div`
  display: flex;
  align-items: center;
  padding: 12px 16px;
`;

const Avatar = styled.img`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  margin-right: 12px;
  object-fit: cover;
`;

const Username = styled.span`
  font-weight: 600;
`;

const MediaContainer = styled.div`
  img, video {
    width: 100%;
    display: block;
  }
`;

const Caption = styled.p`
  padding: 12px 16px;
  margin: 0;
`;

const Tags = styled.div`
  padding: 0 16px 12px;
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
`;

const Tag = styled.span`
  color: #ff4500;
  font-size: 0.9rem;
`;

const Actions = styled.div`
  display: flex;
  padding: 8px 16px;
  border-top: 1px solid #f0f0f0;
`;

const ActionButton = styled.button`
  background: none;
  border: none;
  display: flex;
  align-items: center;
  margin-right: 24px;
  color: #666;
  cursor: pointer;
  font-size: 0.9rem;
  
  svg {
    margin-right: 6px;
    font-size: 1.2rem;
  }

  &:hover {
    color: #ff4500;
  }
`;

const MemeCard = ({ meme }) => {
  return (
    <Card>
      <Header>
        <Avatar 
          src={meme.author.profile?.avatar || 'https://i.pravatar.cc/150?img=3'} 
          alt={meme.author.username}
        />
        <Username>@{meme.author.username}</Username>
      </Header>
      
      <MediaContainer>
        {meme.mediaType === 'image' ? (
          <img src={meme.mediaUrl} alt={meme.caption} />
        ) : (
          <video src={meme.mediaUrl} controls />
        )}
      </MediaContainer>

      {meme.caption && <Caption>{meme.caption}</Caption>}
      
      {meme.tags?.length > 0 && (
        <Tags>
          {meme.tags.map(tag => (
            <Tag key={tag}>#{tag}</Tag>
          ))}
        </Tags>
      )}

      <Actions>
        <ActionButton>
          <FiHeart /> {meme.upvotes || 0}
        </ActionButton>
        <ActionButton>
          <FiMessageSquare /> {meme.commentCount || 0}
        </ActionButton>
        <ActionButton>
          <FiShare2 /> Compartilhar
        </ActionButton>
      </Actions>
    </Card>
  );
};

export default MemeCard;
